// üîß ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
const GITHUB_TOKEN = process.env.GITHUB_TOKEN;
const OWNER = process.env.GITHUB_OWNER || process.env.OWNER;
const REPO = process.env.GITHUB_REPO || process.env.REPO;
