// üêô GitHub Client
const octokit = GITHUB_TOKEN ? new Octokit({ auth: GITHUB_TOKEN }) : null;

/**
 * üéØ ŸÖÿØŸäÿ± ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑŸÖÿ™ŸÇÿØŸÖ
 */
export class AdvancedWorkerManager extends EventEmitter {
    constructor(options = {}) {
        super();

        // ‚öôÔ∏è ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
        this.config = {
            maxConcurrent: options.maxConcurrent || 5,
            maxRetries: options.maxRetries || 3,
            retryDelay: options.retryDelay || 5000,
            watchInterval: options.watchInterval || 3000,
            jobTimeout: options.jobTimeout || 10 * 60 * 1000, // 10 ÿØŸÇÿßÿ¶ŸÇ
            cleanupInterval: options.cleanupInterval || 60 * 60 * 1000, // ÿ≥ÿßÿπÿ©
            enableAutoCleanup: options.enableAutoCleanup !== false,
            enableMetrics: options.enableMetrics !== false,
            projectsDir: options.projectsDir || PROJECTS_DIR
        };

        // üìä ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ
        this.activeJobs = new Map();
        this.jobQueue = [];
        this.isRunning = false;
        this.db = null;
        this.mongoClient = null;

        // üìà ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
        this.stats = {
            processed: 0,
            failed: 0,
            retried: 0,
            avgProcessingTime: 0,
            totalProcessingTime: 0,
            successRate: 0,
            jobsByType: {},
            jobsByStatus: {
                QUEUED: 0,
                WORKING: 0,
                DONE: 0,
                FAILED: 0
            },
            deployments: {
                github: 0,
                cloudflare: 0,
                render: 0,
                vercel: 0
            }
        };

        // üîÑ ÿßŸÑŸÖÿ§ŸÇÿ™ÿßÿ™
        this.watchTimer = null;
        this.cleanupTimer = null;
        this.metricsTimer = null;

        console.log('‚úÖ Advanced Worker Manager initialized');
    }

    /**
     * üöÄ ÿ®ÿØÿ° ÿßŸÑŸÜÿ∏ÿßŸÖ
     */
    async start() {
        if (this.isRunning) {
            console.log('‚ö†Ô∏è Worker Manager already running');
            return;
        }

        try {
            console.log('üöÄ Starting Advanced Worker Manager...');

            // üîå ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
            await this.initialize();

            // ‚úÖ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑŸÜÿ∏ÿßŸÖ
            this.isRunning = true;

            // üîÑ ÿ®ÿØÿ° ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©
            this.startWatching();

            // üßπ ÿ®ÿØÿ° ÿßŸÑÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä
            if (this.config.enableAutoCleanup) {
                this.startAutoCleanup();
            }

            // üìä ÿ®ÿØÿ° ÿ¨ŸÖÿπ ÿßŸÑŸÖŸÇÿßŸäŸäÿ≥
            if (this.config.enableMetrics) {
                this.startMetricsCollection();
            }

            console.log('‚úÖ Worker Manager started successfully');
            console.log(`üìä Max concurrent jobs: ${this.config.maxConcurrent}`);
            console.log(`üîÑ Watch interval: ${this.config.watchInterval}ms`);

            this.emit('started', { timestamp: new Date() });

        } catch (error) {
            console.error('‚ùå Failed to start Worker Manager:', error);
            this.emit('error', { type: 'start_failed', error });
            throw error;
        }
    }

    /**
     * üîå ÿ™ŸáŸäÿ¶ÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
     */
    async initialize() {
        try {
            console.log('üîå Connecting to MongoDB...');

            if (!MONGO_URI) {
                throw new Error('MONGO_URI not configured');
            }

            this.mongoClient = await MongoClient.connect(MONGO_URI, {
                maxPoolSize: 10,
                minPoolSize: 2,
                serverSelectionTimeoutMS: 5000
            });

            this.db = this.mongoClient.db(DB_NAME);

            // üîç ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅŸáÿßÿ±ÿ≥
            await this.createIndexes();

            // üìä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
            await this.loadStats();

            console.log('‚úÖ MongoDB connected successfully');

        } catch (error) {
            console.error('‚ùå MongoDB connection failed:', error);
            throw error;
        }
    }

    /**
     * üîç ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅŸáÿßÿ±ÿ≥
     */
    async createIndexes() {
        try {
            await this.db.collection('jobs').createIndexes([
                { key: { status: 1, createdAt: 1 } },
                { key: { userId: 1, createdAt: -1 } },
                { key: { type: 1 } },
                { key: { completedAt: 1 }, expireAfterSeconds: 30 * 24 * 60 * 60 } // 30 ŸäŸàŸÖ
            ]);

            console.log('‚úÖ Database indexes created');
        } catch (error) {
            console.warn('‚ö†Ô∏è Failed to create indexes:', error.message);
        }
    }

    /**
     * üìä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
     */
    async loadStats() {
        try {
            const pipeline = [
                {
                    $group: {
                        _id: '$status',
                        count: { $sum: 1 }
                    }
                }
            ];

            const results = await this.db.collection('jobs').aggregate(pipeline).toArray();

            results.forEach(result => {
                this.stats.jobsByStatus[result._id] = result.count;
            });

            // ÿ≠ÿ≥ÿßÿ® ŸÖÿπÿØŸÑ ÿßŸÑŸÜÿ¨ÿßÿ≠
            const total = this.stats.jobsByStatus.DONE + this.stats.jobsByStatus.FAILED;
            if (total > 0) {
                this.stats.successRate = (this.stats.jobsByStatus.DONE / total) * 100;
            }

            console.log('üìä Stats loaded:', this.stats.jobsByStatus);

        } catch (error) {
            console.warn('‚ö†Ô∏è Failed to load stats:', error.message);
        }
    }

    /**
     * üîÑ ÿ®ÿØÿ° ÿßŸÑŸÖÿ±ÿßŸÇÿ®ÿ©
     */
    startWatching() {
        this.watchTimer = setInterval(() => {
            this.watchJobs();
        }, this.config.watchInterval);

        // ÿ™ÿ¥ÿ∫ŸäŸÑ ŸÅŸàÿ±Ÿä
        this.watchJobs();

        console.log('üëÄ Job watching started');
    }

    /**
     * üëÄ ŸÖÿ±ÿßŸÇÿ®ÿ© ÿßŸÑŸÖŸáÿßŸÖ
     */
    async watchJobs() {
        if (!this.isRunning) return;

        try {
            // üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖŸáÿßŸÖ ŸÖŸÜÿ™ŸáŸäÿ© ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©
            await this.checkTimeoutJobs();

            // üìä ÿπÿØÿØ ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
            const activeCount = this.activeJobs.size;

            // üéØ ÿπÿØÿØ ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
            const availableSlots = this.config.maxConcurrent - activeCount;

            if (availableSlots <= 0) {
                return; // ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÅÿ™ÿ≠ÿßÿ™ ŸÖÿ™ÿßÿ≠ÿ©
            }

            // üîç ÿ¨ŸÑÿ® ÿßŸÑŸÖŸáÿßŸÖ ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
            const jobs = await this.db.collection('jobs')
                .find({ status: 'QUEUED' })
                .sort({ priority: -1, createdAt: 1 })
                .limit(availableSlots)
                .toArray();

            // üöÄ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸÖŸáÿßŸÖ
            for (const job of jobs) {
                this.processJob(job);
            }

        } catch (error) {
            console.error('‚ùå Watch jobs error:', error);
            this.emit('error', { type: 'watch_jobs', error });
        }
    }

    /**
     * ‚è∞ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖŸáÿßŸÖ ŸÖŸÜÿ™ŸáŸäÿ© ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©
     */
    async checkTimeoutJobs() {
        try {
            const timeoutThreshold = new Date(Date.now() - this.config.jobTimeout);

            const result = await this.db.collection('jobs').updateMany(
                {
                    status: 'WORKING',
                    startedAt: { $lt: timeoutThreshold }
                },
                {
                    $set: {
                        status: 'FAILED',
                        error: 'Job timeout exceeded',
                        failedAt: new Date()
                    }
                }
            );

            if (result.modifiedCount > 0) {
                console.log(`‚è∞ Marked ${result.modifiedCount} jobs as timed out`);
                this.stats.failed += result.modifiedCount;
            }

        } catch (error) {
            console.error('‚ùå Check timeout jobs error:', error);
        }
    }

    /**
     * üîß ŸÖÿπÿßŸÑÿ¨ÿ© ŸÖŸáŸÖÿ©
     */
    async processJob(job) {
        const jobId = job._id.toString();
        const startTime = Date.now();

        // ‚úÖ ÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑŸÖŸáÿßŸÖ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
        this.activeJobs.set(jobId, {
            job,
            startTime,
            attempts: (job.attempts || 0) + 1
        });

        try {
            console.log(`üîß [${jobId}] Processing job: ${job.type || 'unknown'}`);

            // üìù ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿ©
            await this.db.collection('jobs').updateOne(
                { _id: job._id },
                {
                    $set: {
                        status: 'WORKING',
                        startedAt: new Date(),
                        workerId: process.pid
                    },
                    $inc: { attempts: 1 }
                }
            );

            this.emit('job:started', { jobId, job });

            // üéØ ÿ™ÿ≠ÿØŸäÿØ ŸÜŸàÿπ ÿßŸÑŸÖŸáŸÖÿ©
            let result;

            switch (job.type) {
                case 'create_website':
                    result = await this.handleCreateWebsite(job);
                    break;

                case 'create_webapp':
                    result = await this.handleCreateWebApp(job);
                    break;

                case 'create_ecommerce':
                    result = await this.handleCreateEcommerce(job);
                    break;

                case 'improve_code':
                    result = await this.handleImproveCode(job);
                    break;

                case 'deploy_project':
                    result = await this.handleDeployProject(job);
                    break;

                default:
                    // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÜŸàÿπ ŸÖŸÜ ÿßŸÑÿ£ŸÖÿ±
                    result = await this.handleAutoDetect(job);
            }

            // ‚úÖ ÿ≠ŸÅÿ∏ ÿßŸÑŸÜÿ™Ÿäÿ¨ÿ©
            const processingTime = Date.now() - startTime;

            await this.db.collection('jobs').updateOne(
                { _id: job._id },
                {
                    $set: {
                        status: 'DONE',
                        completedAt: new Date(),
                        processingTime,
                        result
                    }
                }
            );

            // üìä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
            this.updateStats(job.type, processingTime, true);

            console.log(`‚úÖ [${jobId}] Job completed in ${processingTime}ms`);
            this.emit('job:completed', { jobId, result, processingTime });

        } catch (error) {
            console.error(`‚ùå [${jobId}] Job failed:`, error);

            // üîÑ ŸÖÿ≠ÿßŸàŸÑÿ© ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ©
            const shouldRetry = this.shouldRetry(job);

            if (shouldRetry) {
                await this.retryJob(job, error);
            } else {
                await this.failJob(job, error);
            }

            this.emit('job:failed', { jobId, error: error.message });

        } finally {
            // üóëÔ∏è ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
            this.activeJobs.delete(jobId);
        }
    }

    /**
     * üåê ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ŸÜÿ¥ÿßÿ° ŸÖŸàŸÇÿπ ŸàŸäÿ®
     */
    async handleCreateWebsite(job) {
        console.log(`üåê Creating website: ${job.title || 'Untitled'}`);

        const projectId = job._id.toString();
        const projectPath = await this.ensureProjectDir(projectId);

        // üé® ÿ™ŸàŸÑŸäÿØ ÿßŸÑŸÖŸàŸÇÿπ
        const htmlCode = await generateWebsite(
            job.description || job.command,
            job.style || 'modern'
        );

        // üíæ ÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÑŸÅÿßÿ™
        await this.saveFile(projectPath, 'index.html', htmlCode);
        await this.saveFile(projectPath, 'README.md', this.generateReadme(job));

        // üì¶ ÿ•ŸÜÿ¥ÿßÿ° ÿ£ÿ±ÿ¥ŸäŸÅ
        const zipPath = await this.createArchive(projectPath, projectId);

        // üöÄ ÿßŸÑŸÜÿ¥ÿ±
        const deployments = await this.deployProject(projectId, projectPath, job);

        return {
            success: true,
            projectId,
            projectPath,
            zipPath,
            files: ['index.html', 'README.md'],
            deployments
        };
    }

    /**
     * üì± ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ∑ÿ®ŸäŸÇ ŸàŸäÿ®
     */
    async handleCreateWebApp(job) {
        console.log(`üì± Creating web app: ${job.title || 'Untitled'}`);

        const projectId = job._id.toString();
        const projectPath = await this.ensureProjectDir(projectId);

        // üé® ÿ™ŸàŸÑŸäÿØ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ
        const result = await generateWebApp(
            job.description || job.command,
            job.features || []
        );

        // üíæ ÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÑŸÅÿßÿ™
        const files = [];
        for (const [filePath, content] of Object.entries(result.files)) {
            await this.saveFile(projectPath, filePath, content);
            files.push(filePath);
        }

        await this.saveFile(projectPath, 'README.md', this.generateReadme(job));
        files.push('README.md');

        // üì¶ ÿ•ŸÜÿ¥ÿßÿ° ÿ£ÿ±ÿ¥ŸäŸÅ
        const zipPath = await this.createArchive(projectPath, projectId);

        // üöÄ ÿßŸÑŸÜÿ¥ÿ±
        const deployments = await this.deployProject(projectId, projectPath, job);

        return {
            success: true,
            projectId,
            projectPath,
            zipPath,
            files,
            deployments
        };
    }

    /**
     * üõí ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ŸÜÿ¥ÿßÿ° ŸÖÿ™ÿ¨ÿ± ÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä
     */
    async handleCreateEcommerce(job) {
        console.log(`üõí Creating e-commerce store: ${job.title || 'Untitled'}`);

        const projectId = job._id.toString();
        const projectPath = await this.ensureProjectDir(projectId);

        // üé® ÿ™ŸàŸÑŸäÿØ ÿßŸÑŸÖÿ™ÿ¨ÿ±
        const result = await generateEcommerce(
            job.description || job.command,
            job.products || []
        );

        // üíæ ÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÑŸÅÿßÿ™
        const files = [];
        for (const [filePath, content] of Object.entries(result.files)) {
            await this.saveFile(projectPath, filePath, content);
            files.push(filePath);
        }

        await this.saveFile(projectPath, 'README.md', this.generateReadme(job));
        files.push('README.md');

        // üì¶ ÿ•ŸÜÿ¥ÿßÿ° ÿ£ÿ±ÿ¥ŸäŸÅ
        const zipPath = await this.createArchive(projectPath, projectId);

        // üöÄ ÿßŸÑŸÜÿ¥ÿ±
        const deployments = await this.deployProject(projectId, projectPath, job);

        return {
            success: true,
            projectId,
            projectPath,
            zipPath,
            files,
            deployments
        };
    }

    /**
     * üîß ŸÖÿπÿßŸÑÿ¨ÿ© ÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑŸÉŸàÿØ
     */
    async handleImproveCode(job) {
        console.log(`üîß Improving code for repo: ${job.repo || 'default'}`);

        // üîç ŸÅÿ≠ÿµ ÿßŸÑŸÖÿ≥ÿ™ŸàÿØÿπ
        const files = await this.scanRepo(job.owner || OWNER, job.repo || REPO);

        const updates = [];

        for (const file of files) {
            if (/\.(html|js|css|jsx|tsx|vue|svelte)$/.test(file.path)) {
                try {
                    const result = await improveCode(
                        file.content,
                        job.command || 'ÿ≠ÿ≥ŸëŸÜ ÿßŸÑŸÉŸàÿØ Ÿàÿ£ÿ∂ŸÅ ÿ™ÿπŸÑŸäŸÇÿßÿ™'
                    );

                    if (result && result.content && result.content.length > 100) {
                        updates.push({
                            path: file.path,
                            content: result.content,
                            message: result.message || `JOE: ÿ™ÿ≠ÿ≥ŸäŸÜ ${file.path}`,
                            sha: file.sha
                        });
                    }
                } catch (error) {
                    console.warn(`‚ö†Ô∏è Failed to improve ${file.path}:`, error.message);
                }
            }
        }

        if (updates.length === 0) {
            throw new Error('No improvements found');
        }

        // üöÄ ÿ±ŸÅÿπ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™
        await this.applyUpdates(updates, job.owner || OWNER, job.repo || REPO);

        return {
            success: true,
            updates: updates.map(u => u.path),
            count: updates.length,
            repo: `https://github.com/${job.owner || OWNER}/${job.repo || REPO}`
        };
    }

    /**
     * üöÄ ŸÖÿπÿßŸÑÿ¨ÿ© ŸÜÿ¥ÿ± ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
     */
    async handleDeployProject(job) {
        console.log(`üöÄ Deploying project: ${job.projectId}`);

        const projectPath = path.join(this.config.projectsDir, job.projectId);

        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
        try {
            await fs.access(projectPath);
        } catch (error) {
            throw new Error(`Project not found: ${job.projectId}`);
        }

        // üöÄ ÿßŸÑŸÜÿ¥ÿ±
        const deployments = await this.deployProject(job.projectId, projectPath, job);

        return {
            success: true,
            projectId: job.projectId,
            deployments
        };
    }

    /**
     * ü§ñ ÿßŸÉÿ™ÿ¥ÿßŸÅ ÿ™ŸÑŸÇÿßÿ¶Ÿä ŸÑŸÜŸàÿπ ÿßŸÑŸÖŸáŸÖÿ©
     */
    async handleAutoDetect(job) {
        const command = (job.command || '').toLowerCase();

        // ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÜŸàÿπ ŸÖŸÜ ÿßŸÑÿ£ŸÖÿ±
        if (command.includes('ÿ£ŸÜÿ¥ÿ¶') || command.includes('create') || command.includes('build')) {
            if (command.includes('ŸÖÿ™ÿ¨ÿ±') || command.includes('shop') || command.includes('ecommerce')) {
                return await this.handleCreateEcommerce(job);
            } else if (command.includes('ÿ™ÿ∑ÿ®ŸäŸÇ') || command.includes('app')) {
                return await this.handleCreateWebApp(job);
            } else {
                return await this.handleCreateWebsite(job);
            }
        } else if (command.includes('ÿ≠ÿ≥ŸëŸÜ') || command.includes('improve') || command.includes('enhance')) {
            return await this.handleImproveCode(job);
        } else if (command.includes('ÿßŸÜÿ¥ÿ±') || command.includes('deploy')) {
            return await this.handleDeployProject(job);
        }

        // ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä: ÿ•ŸÜÿ¥ÿßÿ° ŸÖŸàŸÇÿπ
        return await this.handleCreateWebsite(job);
    }

    /**
     * üöÄ ŸÜÿ¥ÿ± ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
     */
    async deployProject(projectId, projectPath, job) {
        const deployments = {
            github: null,
            cloudflare: null,
            render: null,
            vercel: null
        };

        try {
            // üêô GitHub
            if (job.deployToGithub !== false && GITHUB_TOKEN) {
                try {
                    const githubUrl = await this.deployToGithub(projectPath, job);
                    deployments.github = githubUrl;
                    this.stats.deployments.github++;
                } catch (error) {
                    console.warn('‚ö†Ô∏è GitHub deployment failed:', error.message);
                }
            }

            // ‚òÅÔ∏è Cloudflare
            if (job.deployToCloudflare !== false) {
                try {
                    const cfResult = await deployToCloudflare(
                        projectId,
                        projectPath,
                        job.title || 'joe-project'
                    );
                    if (cfResult.success) {
                        deployments.cloudflare = cfResult.url;
                        this.stats.deployments.cloudflare++;
                    }
                } catch (error) {
                    console.warn('‚ö†Ô∏è Cloudflare deployment failed:', error.message);
                }
            }

            // üé® Render
            if (job.deployToRender) {
                try {
                    const renderResult = await deployToRender(projectId, projectPath, job);
                    if (renderResult.success) {
                        deployments.render = renderResult.url;
                        this.stats.deployments.render++;
                    }
                } catch (error) {
                    console.warn('‚ö†Ô∏è Render deployment failed:', error.message);
                }
            }

            // ‚ñ≤ Vercel
            if (job.deployToVercel) {
                try {
                    const vercelResult = await deployToVercel(projectId, projectPath, job);
                    if (vercelResult.success) {
                        deployments.vercel = vercelResult.url;
                        this.stats.deployments.vercel++;
                    }
                } catch (error) {
                    console.warn('‚ö†Ô∏è Vercel deployment failed:', error.message);
                }
            }

        } catch (error) {
            console.error('‚ùå Deployment error:', error);
        }

        return deployments;
    }

    /**
     * üêô ÿßŸÑŸÜÿ¥ÿ± ÿπŸÑŸâ GitHub
     */
    async deployToGithub(projectPath, job) {
        if (!octokit) {
            throw new Error('GitHub token not configured');
        }

        const owner = job.owner || OWNER;
        const repo = job.repo || REPO || `joe-project-${Date.now()}`;

        // ŸÇÿ±ÿßÿ°ÿ© ÿßŸÑŸÖŸÑŸÅÿßÿ™
        const files = await this.readProjectFiles(projectPath);

        // ÿ±ŸÅÿπ ÿßŸÑŸÖŸÑŸÅÿßÿ™
        for (const file of files) {
            await octokit.repos.createOrUpdateFileContents({
                owner,
                repo,
                path: file.path,
                message: `JOE: ${file.path}`,
                content: Buffer.from(file.content).toString('base64')
            });
        }

        return `https://github.com/${owner}/${repo}`;
    }

    /**
     * üîç ŸÅÿ≠ÿµ ÿßŸÑŸÖÿ≥ÿ™ŸàÿØÿπ
     */
    async scanRepo(owner = OWNER, repo = REPO) {
        if (!octokit) {
            throw new Error('GitHub token not configured');
        }

        try {
            const { data: ref } = await octokit.git.getRef({
                owner,
                repo,
                ref: 'heads/main'
            });

            const { data: commit } = await octokit.git.getCommit({
                owner,
                repo,
                commit_sha: ref.object.sha
            });

            const { data: tree } = await octokit.git.getTree({
                owner,
                repo,
                tree_sha: commit.tree.sha,
                recursive: true
            });

            const codeFiles = tree.tree
                .filter(f => f.type === 'blob' && /\.(js|html|css|json|md|jsx|tsx|vue|svelte)$/.test(f.path))
                .slice(0, 50); // ÿ£ŸàŸÑ 50 ŸÖŸÑŸÅ

            const files = [];
            for (const f of codeFiles) {
                try {
                    const { data } = await octokit.repos.getContent({
                        owner,
                        repo,
                        path: f.path
                    });

                    const content = Buffer.from(data.content, 'base64').toString('utf-8');
                    files.push({
                        path: f.path,
                        content,
                        sha: data.sha
                    });
                } catch (error) {
                    console.warn(`‚ö†Ô∏è Failed to read ${f.path}:`, error.message);
                }
            }

            return files;

        } catch (error) {
            throw new Error(`Failed to scan repo: ${error.message}`);
        }
    }

    /**
     * üöÄ ÿ™ÿ∑ÿ®ŸäŸÇ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™
     */
    async applyUpdates(updates, owner = OWNER, repo = REPO) {
        if (!octokit) {
            throw new Error('GitHub token not configured');
        }

        for (const update of updates) {
            try {
                await octokit.repos.createOrUpdateFileContents({
                    owner,
                    repo,
                    path: update.path,
                    message: update.message,
                    content: Buffer.from(update.content).toString('base64'),
                    sha: update.sha || undefined
                });

                console.log(`‚úÖ Updated: ${update.path}`);
            } catch (error) {
                console.error(`‚ùå Failed to update ${update.path}:`, error.message);
            }
        }
    }

    /**
     * üìÅ ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ŸÖÿ¨ŸÑÿØ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
     */
    async ensureProjectDir(projectId) {
        const projectPath = path.join(this.config.projectsDir, projectId);
        await fs.mkdir(projectPath, { recursive: true });
        return projectPath;
    }

    /**
     * üíæ ÿ≠ŸÅÿ∏ ŸÖŸÑŸÅ
     */
    async saveFile(projectPath, filePath, content) {
        const fullPath = path.join(projectPath, filePath);
        const dir = path.dirname(fullPath);

        await fs.mkdir(dir, { recursive: true });
        await fs.writeFile(fullPath, content, 'utf-8');
    }

    /**
     * üìñ ŸÇÿ±ÿßÿ°ÿ© ŸÖŸÑŸÅÿßÿ™ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
     */
    async readProjectFiles(projectPath) {
        const files = [];

        const readDir = async (dir, basePath = '') => {
            const entries = await fs.readdir(dir, { withFileTypes: true });

            for (const entry of entries) {
                const fullPath = path.join(dir, entry.name);
                const relativePath = path.join(basePath, entry.name);

                if (entry.isDirectory()) {
                    await readDir(fullPath, relativePath);
                } else {
                    const content = await fs.readFile(fullPath, 'utf-8');
                    files.push({
                        path: relativePath,
                        content
                    });
                }
            }
        };

        await readDir(projectPath);
        return files;
    }

    /**
     * üì¶ ÿ•ŸÜÿ¥ÿßÿ° ÿ£ÿ±ÿ¥ŸäŸÅ
     */
    async createArchive(projectPath, projectId) {
        const zipPath = path.join(this.config.projectsDir, `${projectId}.zip`);

        return new Promise((resolve, reject) => {
            const output = createWriteStream(zipPath);
            const archive = archiver('zip', { zlib: { level: 9 } });

            output.on('close', () => resolve(zipPath));
            archive.on('error', reject);

            archive.pipe(output);
            archive.directory(projectPath, false);
            archive.finalize();
        });
    }

    /**
     * üìù ÿ™ŸàŸÑŸäÿØ README
     */
    generateReadme(job) {
        return `# ${job.title || 'Project'}

Generated by JOE - Advanced AI Worker

## Description
${job.description || job.command || 'No description provided'}

## Features
${(job.features || []).map(f => `- ${f}`).join('\n') || '- Modern design\n- Responsive layout\n- SEO optimized'}

## How to use
Simply open \`index.html\` in your browser or deploy to any static hosting service.

---
Generated on: ${new Date().toISOString()}
Project ID: ${job._id}
`;
    }

    /**
     * üîÑ ŸáŸÑ Ÿäÿ¨ÿ® ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ©
     */
    shouldRetry(job) {
        const attempts = job.attempts || 0;
        return attempts < this.config.maxRetries;
    }

    /**
     * üîÑ ÿ•ÿπÿßÿØÿ© ŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑŸÖŸáŸÖÿ©
     */
    async retryJob(job, error) {
        try {
            const retryAt = new Date(Date.now() + this.config.retryDelay);

            await this.db.collection('jobs').updateOne(
                { _id: job._id },
                {
                    $set: {
                        status: 'QUEUED',
                        lastError: error.message,
                        retryAt
                    }
                }
            );

            this.stats.retried++;

            console.log(`üîÑ Job ${job._id} will be retried at ${retryAt}`);
            this.emit('job:retried', { jobId: job._id.toString(), retryAt });

        } catch (err) {
            console.error('‚ùå Failed to retry job:', err);
        }
    }

    /**
     * ‚ùå ŸÅÿ¥ŸÑ ÿßŸÑŸÖŸáŸÖÿ©
     */
    async failJob(job, error) {
        try {
            await this.db.collection('jobs').updateOne(
                { _id: job._id },
                {
                    $set: {
                        status: 'FAILED',
                        error: error.message,
                        failedAt: new Date()
                    }
                }
            );

            this.stats.failed++;

            console.log(`‚ùå Job ${job._id} failed permanently`);

        } catch (err) {
            console.error('‚ùå Failed to mark job as failed:', err);
        }
    }

    /**
     * üìä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
     */
    updateStats(jobType, processingTime, success) {
        if (success) {
            this.stats.processed++;
            this.stats.totalProcessingTime += processingTime;
            this.stats.avgProcessingTime = this.stats.totalProcessingTime / this.stats.processed;
        }

        // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿ≥ÿ® ÿßŸÑŸÜŸàÿπ
        if (jobType) {
            this.stats.jobsByType[jobType] = (this.stats.jobsByType[jobType] || 0) + 1;
        }

        // ÿ≠ÿ≥ÿßÿ® ŸÖÿπÿØŸÑ ÿßŸÑŸÜÿ¨ÿßÿ≠
        const total = this.stats.processed + this.stats.failed;
        if (total > 0) {
            this.stats.successRate = (this.stats.processed / total) * 100;
        }
    }

    /**
     * üßπ ÿ®ÿØÿ° ÿßŸÑÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä
     */
    startAutoCleanup() {
        this.cleanupTimer = setInterval(() => {
            this.cleanup();
        }, this.config.cleanupInterval);

        console.log('üßπ Auto cleanup started');
    }

    /**
     * üßπ ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖÿ¥ÿßÿ±Ÿäÿπ ÿßŸÑŸÇÿØŸäŸÖÿ©
     */
    async cleanup() {
        try {
            console.log('üßπ Running cleanup...');

            // ÿ≠ÿ∞ŸÅ ÿßŸÑŸÖÿ¥ÿßÿ±Ÿäÿπ ÿßŸÑŸÇÿØŸäŸÖÿ© (ÿ£ŸÉÿ´ÿ± ŸÖŸÜ 7 ÿ£ŸäÿßŸÖ)
            const sevenDaysAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

            const oldJobs = await this.db.collection('jobs')
                .find({
                    status: { $in: ['DONE', 'FAILED'] },
                    completedAt: { $lt: sevenDaysAgo }
                })
                .toArray();

            for (const job of oldJobs) {
                const projectPath = path.join(this.config.projectsDir, job._id.toString());
                const zipPath = path.join(this.config.projectsDir, `${job._id}.zip`);

                // ÿ≠ÿ∞ŸÅ ÿßŸÑŸÖÿ¨ŸÑÿØ
                await fs.rm(projectPath, { recursive: true, force: true }).catch(() => {});

                // ÿ≠ÿ∞ŸÅ ÿßŸÑÿ£ÿ±ÿ¥ŸäŸÅ
                await fs.rm(zipPath, { force: true }).catch(() => {});
            }

            console.log(`üßπ Cleaned up ${oldJobs.length} old projects`);
            this.emit('cleanup:completed', { count: oldJobs.length });

        } catch (error) {
            console.error('‚ùå Cleanup error:', error);
            this.emit('error', { type: 'cleanup', error });
        }
    }

    /**
     * üìä ÿ®ÿØÿ° ÿ¨ŸÖÿπ ÿßŸÑŸÖŸÇÿßŸäŸäÿ≥
     */
    startMetricsCollection() {
        this.metricsTimer = setInterval(() => {
            this.collectMetrics();
        }, 60 * 1000); // ŸÉŸÑ ÿØŸÇŸäŸÇÿ©

        console.log('üìä Metrics collection started');
    }

    /**
     * üìä ÿ¨ŸÖÿπ ÿßŸÑŸÖŸÇÿßŸäŸäÿ≥
     */
    async collectMetrics() {
        try {
            const metrics = {
                timestamp: new Date(),
                activeJobs: this.activeJobs.size,
                stats: { ...this.stats },
                memory: process.memoryUsage(),
                uptime: process.uptime()
            };

            await this.db.collection('worker_metrics').insertOne(metrics);

            this.emit('metrics:collected', metrics);

        } catch (error) {
            console.error('‚ùå Metrics collection error:', error);
        }
    }

    /**
     * üìä ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
     */
    getStats() {
        return {
            ...this.stats,
            activeJobs: this.activeJobs.size,
            isRunning: this.isRunning,
            uptime: process.uptime(),
            memory: process.memoryUsage()
        };
    }

    /**
     * ‚èπÔ∏è ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÜÿ∏ÿßŸÖ
     */
    async stop() {
        console.log('‚èπÔ∏è Stopping Worker Manager...');

        this.isRunning = false;

        // ÿ•ŸäŸÇÿßŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™ÿßÿ™
        if (this.watchTimer) clearInterval(this.watchTimer);
        if (this.cleanupTimer) clearInterval(this.cleanupTimer);
        if (this.metricsTimer) clearInterval(this.metricsTimer);

        // ÿßŸÜÿ™ÿ∏ÿßÿ± ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
        if (this.activeJobs.size > 0) {
            console.log(`‚è≥ Waiting for ${this.activeJobs.size} active jobs...`);
            await new Promise(resolve => setTimeout(resolve, 5000));
        }

        // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿßÿ™ÿµÿßŸÑ
        if (this.mongoClient) {
            await this.mongoClient.close();
        }

        this.emit('stopped');
        console.log('‚úÖ Worker Manager stopped');
    }
}

