import { WebSocketServer } from 'ws';
import BrowserController from './browserController.mjs';

class BrowserWebSocketServer {
  constructor(server) {
    this.wss = new WebSocketServer({ server, path: '/ws/browser' });
    this.browserController = new BrowserController();
    this.clients = new Set();
    this.screenshotInterval = null;

    this.setupWebSocket();
  }

  setupWebSocket() {
    this.wss.on('connection', (ws) => {
      console.log('Browser WebSocket client connected');
      this.clients.add(ws);

      // Initialize browser on first connection
      this.browserController.initialize().catch(err => {
        console.error('Failed to initialize browser:', err);
        ws.send(JSON.stringify({ type: 'error', message: 'Failed to initialize browser' }));
      });

      ws.on('message', async (message) => {
        try {
          const data = JSON.parse(message);
          await this.handleMessage(ws, data);
        } catch (error) {
          console.error('WebSocket message error:', error);
          ws.send(JSON.stringify({ type: 'error', message: error.message }));
        }
      });

      ws.on('close', () => {
        console.log('Browser WebSocket client disconnected');
        this.clients.delete(ws);

        // Stop screenshot streaming if no clients
        if (this.clients.size === 0 && this.screenshotInterval) {
          clearInterval(this.screenshotInterval);
          this.screenshotInterval = null;
        }
      });

      ws.on('error', (error) => {
        console.error('WebSocket error:', error);
      });
    });
  }

  async handleMessage(ws, data) {
    const { type, payload } = data;

    switch (type) {
      case 'navigate':
        const navResult = await this.browserController.navigate(payload.url);
        ws.send(JSON.stringify({ type: 'navigate_result', payload: navResult }));

        // Send initial screenshot after navigation
        setTimeout(async () => {
          const screenshot = await this.browserController.getScreenshot();
          const pageInfo = await this.browserController.getPageInfo();
          ws.send(JSON.stringify({ 
            type: 'screenshot', 
            payload: { screenshot, pageInfo } 
          }));
        }, 1000);
        break;

      case 'click':
        const clickResult = await this.browserController.click(payload.x, payload.y);
        ws.send(JSON.stringify({ type: 'click_result', payload: clickResult }));

        // Send screenshot after click
        setTimeout(async () => {
          const screenshot = await this.browserController.getScreenshot();
          ws.send(JSON.stringify({ type: 'screenshot', payload: { screenshot } }));
        }, 500);
        break;

      case 'type':
        const typeResult = await this.browserController.type(payload.text);
        ws.send(JSON.stringify({ type: 'type_result', payload: typeResult }));
        break;

      case 'scroll':
        const scrollResult = await this.browserController.scroll(payload.deltaY);
        ws.send(JSON.stringify({ type: 'scroll_result', payload: scrollResult }));

        // Send screenshot after scroll
        setTimeout(async () => {
          const screenshot = await this.browserController.getScreenshot();
          ws.send(JSON.stringify({ type: 'screenshot', payload: { screenshot } }));
        }, 300);
        break;

      case 'press_key':
        const keyResult = await this.browserController.pressKey(payload.key);
        ws.send(JSON.stringify({ type: 'press_key_result', payload: keyResult }));
        break;

      case 'get_screenshot':
        const screenshot = await this.browserController.getScreenshot();
        const pageInfo = await this.browserController.getPageInfo();
        ws.send(JSON.stringify({ 
          type: 'screenshot', 
          payload: { screenshot, pageInfo } 
        }));
        break;

      case 'start_streaming':
        // Start continuous screenshot streaming
        if (!this.screenshotInterval) {
          this.screenshotInterval = setInterval(async () => {
            try {
              const screenshot = await this.browserController.getScreenshot();
              const pageInfo = await this.browserController.getPageInfo();
              this.broadcast({ 
                type: 'screenshot', 
                payload: { screenshot, pageInfo } 
              });
            } catch (error) {
              console.error('Screenshot streaming error:', error);
            }
          }, 500); // Update every 0.5 seconds for better responsiveness
        }
        break;

      case 'stop_streaming':
        if (this.screenshotInterval) {
          clearInterval(this.screenshotInterval);
          this.screenshotInterval = null;
        }
        break;

      default:
        ws.send(JSON.stringify({ type: 'error', message: 'Unknown message type' }));
    }
  }

  broadcast(data) {
    const message = JSON.stringify(data);
    this.clients.forEach(client => {
      if (client.readyState === 1) { // WebSocket.OPEN
        client.send(message);
      }
    });
  }

  async close() {
    if (this.screenshotInterval) {
      clearInterval(this.screenshotInterval);
    }
    await this.browserController.close();
    this.wss.close();
  }
}

export default BrowserWebSocketServer;

/**
 * ‚òÅÔ∏è Cloudflare Deployer - ŸÜÿ∏ÿßŸÖ ÿßŸÑŸÜÿ¥ÿ± ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä ÿßŸÑŸÖÿ™ŸÇÿØŸÖ ÿπŸÑŸâ Cloudflare Pages
 * ŸÜÿ∏ÿßŸÖ ŸÖÿ™ÿ∑Ÿàÿ± ŸÑŸÑŸÜÿ¥ÿ± ŸÖÿπ ÿØÿπŸÖ Workers Ÿà KV Ÿà R2
 * ŸÖÿ™ŸàÿßŸÅŸÇ ŸÖÿπ Joe Advanced Engine Ÿà Gemini Engine
 * 
 * @module CloudflareDeployer
 * @version 2.0.0
 */

import fs from 'fs/promises';
import path from 'path';
import { exec } from 'child_process';
import { promisify } from 'util';
import { createHash } from 'crypto';
import archiver from 'archiver';
import FormData from 'form-data';

const execAsync = promisify(exec);

// üîë ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßÿπÿ™ŸÖÿßÿØ
const CF_ACCOUNT_ID = process.env.CLOUDFLARE_ACCOUNT_ID;
const CF_API_TOKEN = process.env.CLOUDFLARE_API_TOKEN;
const CF_API_BASE = 'https://api.cloudflare.com/client/v4';

/**
 * üéØ ŸÅÿ¶ÿ© Cloudflare Deployer ÿßŸÑŸÖÿ™ŸÇÿØŸÖÿ©
 */
export class CloudflareDeployer {
  constructor(options = {}) {
    this.accountId = options.accountId || CF_ACCOUNT_ID;
    this.apiToken = options.apiToken || CF_API_TOKEN;
    this.config = {
      maxRetries: options.maxRetries || 3,
      retryDelay: options.retryDelay || 2000,
      timeout: options.timeout || 300000, // 5 ÿØŸÇÿßÿ¶ŸÇ
      maxFileSize: options.maxFileSize || 25 * 1024 * 1024, // 25MB
      ...options
    };

    // üìä ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
    this.stats = {
      totalDeployments: 0,
      successfulDeployments: 0,
      failedDeployments: 0,
      totalFilesUploaded: 0,
      totalBytesUploaded: 0
    };

    // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßÿπÿ™ŸÖÿßÿØ
    if (!this.accountId || !this.apiToken) {
      console.warn('‚ö†Ô∏è Cloudflare credentials not configured');
    } else {
      console.log('‚úÖ Cloudflare Deployer initialized');
    }
  }

  /**
   * üöÄ ŸÜÿ¥ÿ± ŸÖÿ¥ÿ±Ÿàÿπ ÿπŸÑŸâ Cloudflare Pages
   * @param {string} projectId - ŸÖÿπÿ±ŸÅ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
   * @param {string} projectPath - ŸÖÿ≥ÿßÿ± ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
   * @param {string} projectName - ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
   * @param {object} options - ÿÆŸäÿßÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ±
   * @returns {Promise<object>} - ŸÜÿ™Ÿäÿ¨ÿ© ÿßŸÑŸÜÿ¥ÿ±
   */
  async deployToCloudflare(projectId, projectPath, projectName, options = {}) {
    const startTime = Date.now();

    try {
      console.log(`‚òÅÔ∏è [CloudflareDeployer] ÿ®ÿØÿ° ÿßŸÑŸÜÿ¥ÿ±: ${projectId}`);
      console.log(`üìÅ ÿßŸÑŸÖÿ≥ÿßÿ±: ${projectPath}`);
      console.log(`üìù ÿßŸÑÿßÿ≥ŸÖ: ${projectName}`);

      // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßÿπÿ™ŸÖÿßÿØ
      if (!this.accountId || !this.apiToken) {
        throw new Error('Cloudflare credentials not configured. Please set CLOUDFLARE_ACCOUNT_ID and CLOUDFLARE_API_TOKEN');
      }

      // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
      const exists = await this.checkPathExists(projectPath);
      if (!exists) {
        throw new Error(`Project path does not exist: ${projectPath}`);
      }

      // üé® ÿ•ŸÜÿ¥ÿßÿ° ÿßÿ≥ŸÖ ÿ¢ŸÖŸÜ ŸÑŸÑŸÜÿ¥ÿ±
      const deploymentName = this.createSafeName(projectName, projectId);
      console.log(`üè∑Ô∏è ÿßÿ≥ŸÖ ÿßŸÑŸÜÿ¥ÿ±: ${deploymentName}`);

      // üîç ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
      const projectInfo = await this.analyzeProject(projectPath);
      console.log(`üìä ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ:`, projectInfo);

      // üéØ ÿßÿÆÿ™Ÿäÿßÿ± ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑŸÜÿ¥ÿ±
      let result;

      if (options.useAPI || projectInfo.totalSize > 100 * 1024 * 1024) {
        // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ API ŸÑŸÑŸÖÿ¥ÿßÿ±Ÿäÿπ ÿßŸÑŸÉÿ®Ÿäÿ±ÿ©
        console.log('üîß ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Cloudflare API ŸÑŸÑŸÜÿ¥ÿ±...');
        result = await this.deployViaAPI(projectId, projectPath, deploymentName, options);
      } else {
        // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Wrangler CLI
        console.log('‚ö° ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Wrangler CLI ŸÑŸÑŸÜÿ¥ÿ±...');
        result = await this.deployViaWrangler(projectPath, deploymentName, options);
      }

      // üìä ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿ∫ÿ±ŸÇ
      const duration = Date.now() - startTime;

      // üìà ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      this.stats.totalDeployments++;
      if (result.success) {
        this.stats.successfulDeployments++;
      } else {
        this.stats.failedDeployments++;
      }

      console.log(`‚úÖ ÿßŸÑŸÜÿ¥ÿ± ÿßŸÉÿ™ŸÖŸÑ ŸÅŸä ${(duration / 1000).toFixed(2)}s`);

      return {
        ...result,
        projectId,
        deploymentName,
        duration,
        projectInfo,
        timestamp: new Date().toISOString()
      };

    } catch (error) {
      this.stats.totalDeployments++;
      this.stats.failedDeployments++;

      console.error('‚ùå [CloudflareDeployer] ŸÅÿ¥ŸÑ ÿßŸÑŸÜÿ¥ÿ±:', error);

      return {
        success: false,
        error: error.message,
        stack: process.env.NODE_ENV === 'development' ? error.stack : undefined,
        projectId,
        timestamp: new Date().toISOString()
      };
    }
  }

  /**
   * ‚ö° ÿßŸÑŸÜÿ¥ÿ± ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Wrangler CLI
   */
  async deployViaWrangler(projectPath, deploymentName, options = {}) {
    try {
      // üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ™ÿ´ÿ®Ÿäÿ™ Wrangler
      try {
        await execAsync('npx wrangler --version');
      } catch (error) {
        console.log('üì¶ ÿ™ÿ´ÿ®Ÿäÿ™ Wrangler...');
        await execAsync('npm install -g wrangler');
      }

      // üîß ÿ®ŸÜÿßÿ° ÿßŸÑÿ£ŸÖÿ±
      const command = this.buildWranglerCommand(projectPath, deploymentName, options);
      console.log(`üîß ÿßŸÑÿ£ŸÖÿ±: ${command}`);

      // üöÄ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑŸÜÿ¥ÿ±
      const { stdout, stderr } = await execAsync(command, {
        env: {
          ...process.env,
          CLOUDFLARE_ACCOUNT_ID: this.accountId,
          CLOUDFLARE_API_TOKEN: this.apiToken
        },
        cwd: projectPath,
        timeout: this.config.timeout,
        maxBuffer: 10 * 1024 * 1024 // 10MB buffer
      });

      console.log('üìã ŸÖÿÆÿ±ÿ¨ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ±:', stdout);

      if (stderr && !stderr.toLowerCase().includes('warning')) {
        console.warn('‚ö†Ô∏è ÿ™ÿ≠ÿ∞Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ±:', stderr);
      }

      // üîç ÿßÿ≥ÿ™ÿÆÿ±ÿßÿ¨ URL
      const deploymentUrl = this.extractDeploymentUrl(stdout, deploymentName);

      // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÜÿ¥ÿ±
      const isLive = await this.verifyDeployment(deploymentUrl);

      return {
        success: true,
        url: deploymentUrl,
        deploymentName,
        method: 'wrangler',
        verified: isLive,
        output: stdout
      };

    } catch (error) {
      console.error('‚ùå Wrangler deployment failed:', error);

      // üîÑ ŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑŸÜÿ¥ÿ± ÿπÿ®ÿ± API ŸÉŸÄ fallback
      if (options.fallbackToAPI !== false) {
        console.log('üîÑ ŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑŸÜÿ¥ÿ± ÿπÿ®ÿ± API...');
        return await this.deployViaAPI(null, projectPath, deploymentName, options);
      }

      throw error;
    }
  }

  /**
   * üåê ÿßŸÑŸÜÿ¥ÿ± ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Cloudflare API ŸÖÿ®ÿßÿ¥ÿ±ÿ©
   */
  async deployViaAPI(projectId, projectPath, deploymentName, options = {}) {
    try {
      console.log('üåê [CloudflareDeployer] ÿßŸÑŸÜÿ¥ÿ± ÿπÿ®ÿ± API...');

      // üì¶ ÿ•ŸÜÿ¥ÿßÿ° ÿ£Ÿà ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
      const project = await this.ensureProject(deploymentName, options);
      console.log('‚úÖ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ ÿ¨ÿßŸáÿ≤:', project.name);

      // üìÅ ŸÇÿ±ÿßÿ°ÿ© Ÿàÿ™ÿ≠ÿ∂Ÿäÿ± ÿßŸÑŸÖŸÑŸÅÿßÿ™
      const files = await this.prepareFiles(projectPath, options);
      console.log(`üìÅ ÿπÿØÿØ ÿßŸÑŸÖŸÑŸÅÿßÿ™: ${files.length}`);

      // üóúÔ∏è ÿ∂ÿ∫ÿ∑ ÿßŸÑŸÖŸÑŸÅÿßÿ™
      const zipBuffer = await this.createZipArchive(files, projectPath);
      console.log(`üóúÔ∏è ÿ≠ÿ¨ŸÖ ÿßŸÑÿ£ÿ±ÿ¥ŸäŸÅ: ${(zipBuffer.length / 1024 / 1024).toFixed(2)} MB`);

      // üì§ ÿ±ŸÅÿπ ÿßŸÑŸÜÿ¥ÿ±
      const deployment = await this.uploadDeployment(deploymentName, zipBuffer, options);
      console.log('‚úÖ ÿ™ŸÖ ÿ±ŸÅÿπ ÿßŸÑŸÜÿ¥ÿ±:', deployment.id);

      // ‚è≥ ÿßŸÜÿ™ÿ∏ÿßÿ± ÿßŸÉÿ™ŸÖÿßŸÑ ÿßŸÑŸÜÿ¥ÿ±
      const finalDeployment = await this.waitForDeployment(
        deploymentName,
        deployment.id,
        options.timeout || 300000
      );

      // üìä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      this.stats.totalFilesUploaded += files.length;
      this.stats.totalBytesUploaded += zipBuffer.length;

      const deploymentUrl = finalDeployment.url || `https://${deploymentName}.pages.dev`;

      return {
        success: true,
        url: deploymentUrl,
        deploymentName,
        deploymentId: deployment.id,
        method: 'api',
        status: finalDeployment.latest_stage?.status,
        filesCount: files.length,
        totalSize: zipBuffer.length
      };

    } catch (error) {
      console.error('‚ùå API deployment failed:', error);
      throw error;
    }
  }

  /**
   * üîß ÿ®ŸÜÿßÿ° ÿ£ŸÖÿ± Wrangler
   */
  buildWranglerCommand(projectPath, deploymentName, options = {}) {
    const parts = [
      'npx wrangler pages deploy',
      projectPath,
      `--project-name=${deploymentName}`
    ];

    if (options.branch) {
      parts.push(`--branch=${options.branch}`);
    }

    if (options.commitMessage) {
      parts.push(`--commit-message="${options.commitMessage}"`);
    }

