  /**
   * üìÅ ÿ™ÿ≠ÿ∂Ÿäÿ± ÿßŸÑŸÖŸÑŸÅÿßÿ™ ŸÑŸÑŸÜÿ¥ÿ±
   */
  async prepareFiles(projectPath, options = {}) {
    const files = await this.readDirectoryRecursive(projectPath);

    // üîç ÿ™ÿµŸÅŸäÿ© ÿßŸÑŸÖŸÑŸÅÿßÿ™
    const filteredFiles = files.filter(file => {
      const relativePath = path.relative(projectPath, file);

      // ÿ™ÿ¨ÿßŸáŸÑ ÿßŸÑŸÖŸÑŸÅÿßÿ™ ŸàÿßŸÑŸÖÿ¨ŸÑÿØÿßÿ™ ÿ∫Ÿäÿ± ÿßŸÑŸÖÿ±ÿ∫Ÿàÿ®ÿ©
      const ignored = [
        'node_modules',
        '.git',
        '.env',
        '.DS_Store',
        'Thumbs.db',
        '*.log',
        '.cache',
        'dist',
        'build'
      ];

      return !ignored.some(pattern => {
        if (pattern.includes('*')) {
          const regex = new RegExp(pattern.replace('*', '.*'));
          return regex.test(relativePath);
        }
        return relativePath.includes(pattern);
      });
    });

    // üìä ÿ≠ÿ≥ÿßÿ® ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖŸÑŸÅÿßÿ™
    const filesWithInfo = await Promise.all(
      filteredFiles.map(async (file) => {
        const stats = await fs.stat(file);
        const content = await fs.readFile(file);
        const relativePath = path.relative(projectPath, file);

        return {
          path: file,
          relativePath,
          size: stats.size,
          content,
          hash: createHash('sha256').update(content).digest('hex')
        };
      })
    );

    return filesWithInfo;
  }

  /**
   * üóúÔ∏è ÿ•ŸÜÿ¥ÿßÿ° ÿ£ÿ±ÿ¥ŸäŸÅ ZIP
   */
  async createZipArchive(files, basePath) {
    return new Promise((resolve, reject) => {
      const chunks = [];
      const archive = archiver('zip', {
        zlib: { level: 9 } // ÿ£ŸÇÿµŸâ ÿ∂ÿ∫ÿ∑
      });

      archive.on('data', chunk => chunks.push(chunk));
      archive.on('end', () => resolve(Buffer.concat(chunks)));
      archive.on('error', reject);

      // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖŸÑŸÅÿßÿ™
      for (const file of files) {
        archive.append(file.content, { name: file.relativePath });
      }

      archive.finalize();
    });
  }

  /**
