  }

  /**
   * ‚è≥ ÿßŸÜÿ™ÿ∏ÿßÿ± ÿßŸÉÿ™ŸÖÿßŸÑ ÿßŸÑŸÜÿ¥ÿ±
   */
  async waitForDeployment(projectName, deploymentId, timeout = 300000) {
    const startTime = Date.now();
    const pollInterval = 5000; // 5 ÿ´ŸàÿßŸÜŸä

    while (Date.now() - startTime < timeout) {
      try {
        const deployment = await this.getDeployment(projectName, deploymentId);
        const status = deployment.latest_stage?.status;

        console.log(`‚è≥ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ¥ÿ±: ${status}`);

        if (status === 'success') {
          console.log('‚úÖ ÿßŸÑŸÜÿ¥ÿ± ÿßŸÉÿ™ŸÖŸÑ ÿ®ŸÜÿ¨ÿßÿ≠');
          return deployment;
        }

        if (status === 'failure' || status === 'canceled') {
          throw new Error(`Deployment ${status}: ${deployment.latest_stage?.name}`);
        }

        // ÿßŸÜÿ™ÿ∏ÿßÿ± ŸÇÿ®ŸÑ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ™ÿßŸÑŸäÿ©
        await this.delay(pollInterval);

      } catch (error) {
        if (Date.now() - startTime >= timeout) {
          throw new Error('Deployment timeout');
        }
        throw error;
      }
    }

    throw new Error('Deployment timeout');
  }

  /**
   * üìã ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÜÿ¥ÿ±
