      return { success: false, error: error.message };
    }
  }

  /**
   * üîß ÿ∑ŸÑÿ® HTTP ŸÖÿπ ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ©
   */
  async makeRequest(url, options = {}, retries = 0) {
    try {
      const response = await fetch(url, {
        ...options,
        headers: {
          'Authorization': `Bearer ${this.apiToken}`,
          'Content-Type': 'application/json',
          ...options.headers
        }
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(
          data.errors?.[0]?.message || 
          `API request failed: ${response.status} ${response.statusText}`
        );
      }

      return data;

    } catch (error) {
      if (retries < this.config.maxRetries) {
        console.log(`üîÑ ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ${retries + 1}/${this.config.maxRetries}...`);
        await this.delay(this.config.retryDelay);
        return this.makeRequest(url, options, retries + 1);
      }

      throw error;
    }
  }

  /**
   * üìä ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑŸÖÿ¥ÿ±Ÿàÿπ
   */
  async analyzeProject(projectPath) {
    const files = await this.readDirectoryRecursive(projectPath);

    let totalSize = 0;
    const fileTypes = {};

    for (const file of files) {
      const stats = await fs.stat(file);
      totalSize += stats.size;

      const ext = path.extname(file).toLowerCase();
      fileTypes[ext] = (fileTypes[ext] || 0) + 1;
    }

    return {
      totalFiles: files.length,
      totalSize,
      fileTypes,
      averageFileSize: totalSize / files.length
    };
  }

  /**
   * üìÅ ŸÇÿ±ÿßÿ°ÿ© ÿßŸÑŸÖÿ¨ŸÑÿØ ÿ®ÿ¥ŸÉŸÑ ŸÖÿ™ŸÉÿ±ÿ±
   */
  async readDirectoryRecursive(dir) {
    const files = [];
    const items = await fs.readdir(dir, { withFileTypes: true });

    for (const item of items) {
      const fullPath = path.join(dir, item.name);

      if (item.isDirectory()) {
        if (!['node_modules', '.git', '.cache'].includes(item.name)) {
          const subFiles = await this.readDirectoryRecursive(fullPath);
          files.push(...subFiles);
        }
      } else {
        files.push(fullPath);
      }
    }

    return files;
  }

  /**
   * üé® ÿ•ŸÜÿ¥ÿßÿ° ÿßÿ≥ŸÖ ÿ¢ŸÖŸÜ
   */
  createSafeName(projectName, projectId) {
    const safeName = projectName
      .toLowerCase()
      .replace(/[^a-z0-9-]/g, '-')
      .replace(/-+/g, '-')
      .replace(/^-|-$/g, '')
      .substring(0, 50);

    const shortId = projectId ? projectId.substring(0, 8) : Date.now().toString(36);

    return `${safeName}-${shortId}`;
  }

  /**
   * ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ŸÖÿ≥ÿßÿ±
   */
  async checkPathExists(filePath) {
    try {
      await fs.access(filePath);
      return true;
    } catch {
      return false;
    }
  }

  /**
   * ‚è±Ô∏è ÿ™ÿ£ÿÆŸäÿ±
   */
  async delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * üìä ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
   */
  getStats() {
    return {
      ...this.stats,
      successRate: this.stats.totalDeployments > 0
        ? ((this.stats.successfulDeployments / this.stats.totalDeployments) * 100).toFixed(2) + '%'
        : '0%'
    };
  }
}

