import { EventEmitter } from 'events';
import path from 'path';
import { fileURLToPath } from 'url';
import sharp from 'sharp';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

/**
 * ÿÆÿØŸÖÿ© ÿßŸÑÿ®ÿ´ ÿßŸÑÿ≠Ÿä ŸÑÿπÿ±ÿ∂ ÿ≥ÿ∑ÿ≠ ÿßŸÑŸÖŸÉÿ™ÿ®
 * ÿ™ŸÇŸàŸÖ ÿ®ÿßŸÑÿ™ŸÇÿßÿ∑ ŸÑŸÇÿ∑ÿßÿ™ ÿ¥ÿßÿ¥ÿ© Ÿàÿ•ÿ±ÿ≥ÿßŸÑŸáÿß ÿπÿ®ÿ± WebSocket
 */
export class LiveStreamingService extends EventEmitter {
  constructor() {
    super();
    this.isStreaming = false;
    this.captureProcess = null;
    this.frameRate = 2; // ÿπÿØÿØ ÿßŸÑÿ•ÿ∑ÿßÿ±ÿßÿ™ ŸÅŸä ÿßŸÑÿ´ÿßŸÜŸäÿ©
    this.quality = 70; // ÿ¨ŸàÿØÿ© ÿßŸÑÿµŸàÿ±ÿ© (1-100)
    this.width = 1280;
    this.height = 720;
    this.lastFrameTime = 0;
    this.frameInterval = 1000 / this.frameRate;
    this.subscribers = new Set();
    this.currentScreen = null;
    this.stats = {
      totalFrames: 0,
      fps: 0,
      bandwidth: 0,
      startTime: null,
      uptime: 0
    };
  }

  /**
   * ÿ®ÿØÿ° ÿßŸÑÿ®ÿ´ ÿßŸÑÿ≠Ÿä
   */
  async startStreaming() {
    if (this.isStreaming) {
      console.warn('ÿßŸÑÿ®ÿ´ ÿßŸÑÿ≠Ÿä ŸÇŸäÿØ ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ ÿ®ÿßŸÑŸÅÿπŸÑ');
      return;
    }

    this.isStreaming = true;
    this.stats.startTime = Date.now();
    this.emit('stream-started');

    // ŸÖÿ≠ÿßŸÉÿßÿ© ÿßŸÑÿ™ŸÇÿßÿ∑ ÿßŸÑÿ¥ÿßÿ¥ÿ©
    this.captureScreenLoop();
  }

  /**
   * ÿ•ŸäŸÇÿßŸÅ ÿßŸÑÿ®ÿ´ ÿßŸÑÿ≠Ÿä
   */
  stopStreaming() {
    if (!this.isStreaming) {
      console.warn('ÿßŸÑÿ®ÿ´ ÿßŸÑÿ≠Ÿä ÿ∫Ÿäÿ± ŸÖŸÅÿπŸÑ');
      return;
    }

    this.isStreaming = false;
    if (this.captureProcess) {
      this.captureProcess.kill();
      this.captureProcess = null;
    }
    this.emit('stream-stopped');
  }

  /**
   * ÿ≠ŸÑŸÇÿ© ÿßŸÑÿ™ŸÇÿßÿ∑ ÿßŸÑÿ¥ÿßÿ¥ÿ©
   */
  async captureScreenLoop() {
    while (this.isStreaming) {
      const now = Date.now();

      if (now - this.lastFrameTime >= this.frameInterval) {
        try {
          const frame = await this.captureScreen();
          if (frame) {
            this.currentScreen = frame;
            this.broadcastFrame(frame);
            this.stats.totalFrames++;
            this.lastFrameTime = now;
          }
        } catch (error) {
          console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ŸÇÿßÿ∑ ÿßŸÑÿ¥ÿßÿ¥ÿ©:', error);
        }
      }

      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      this.updateStats();

      // ÿßŸÜÿ™ÿ∏ÿ± ŸÇŸÑŸäŸÑÿßŸã ŸÇÿ®ŸÑ ÿßŸÑÿ•ÿ∑ÿßÿ± ÿßŸÑÿ™ÿßŸÑŸä
      await new Promise(resolve => setTimeout(resolve, 50));
    }
  }

  /**
   * ÿßŸÑÿ™ŸÇÿßÿ∑ ŸÑŸÇÿ∑ÿ© ÿ¥ÿßÿ¥ÿ©
   */
  async captureScreen() {
    try {
      // ŸÅŸä ÿ®Ÿäÿ¶ÿ© ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ÿå ŸäŸÖŸÉŸÜ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ£ÿØŸàÿßÿ™ ŸÖÿ´ŸÑ:
      // - ffmpeg
      // - gnome-screenshot
      // - scrot
      // - ImageMagick

      // ŸáŸÜÿß ŸÜŸÇŸàŸÖ ÿ®ÿ•ŸÜÿ¥ÿßÿ° ÿµŸàÿ±ÿ© ŸàŸáŸÖŸäÿ© ŸÑŸÑÿßÿÆÿ™ÿ®ÿßÿ±
      const canvas = await this.generateScreenCapture();

      // ÿ∂ÿ∫ÿ∑ ÿßŸÑÿµŸàÿ±ÿ©
      const compressed = await sharp(canvas)
        .resize(this.width, this.height, {
          fit: 'cover',
          position: 'center'
        })
        .jpeg({ quality: this.quality })
        .toBuffer();

      return compressed;
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ŸÇÿßÿ∑ ÿßŸÑÿ¥ÿßÿ¥ÿ©:', error);
      return null;
    }
  }

  /**
   * ÿ™ŸàŸÑŸäÿØ ÿµŸàÿ±ÿ© ŸàŸáŸÖŸäÿ© ŸÑŸÑÿ¥ÿßÿ¥ÿ© (ŸÑŸÑÿßÿÆÿ™ÿ®ÿßÿ±)
   */
  async generateScreenCapture() {
    // ŸÅŸä ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ÿå ÿ≥Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿ®ÿØÿßŸÑ Ÿáÿ∞ÿß ÿ®ÿßŸÑÿ™ŸÇÿßÿ∑ ÿ≠ŸÇŸäŸÇŸä
    const width = this.width;
    const height = this.height;

    // ÿ•ŸÜÿ¥ÿßÿ° ÿµŸàÿ±ÿ© ÿ®ÿ≥Ÿäÿ∑ÿ©
    const svg = `
      <svg width="${width}" height="${height}" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <linearGradient id="grad1" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" style="stop-color:#1a1a2e;stop-opacity:1" />
            <stop offset="100%" style="stop-color:#16213e;stop-opacity:1" />
          </linearGradient>
        </defs>
        <rect width="${width}" height="${height}" fill="url(#grad1)"/>

        <!-- ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑÿπŸÑŸàŸä -->
        <rect width="${width}" height="50" fill="#0f3460" opacity="0.8"/>
        <text x="20" y="35" font-family="Arial" font-size="20" fill="#00d4ff" font-weight="bold">
          Jo AI System - Live Stream
        </text>
        <text x="${width - 200}" y="35" font-family="Arial" font-size="14" fill="#00d4ff">
          ${new Date().toLocaleTimeString('ar-SA')}
        </text>

        <!-- ŸÖŸÜÿ∑ŸÇÿ© ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© -->
        <rect x="20" y="70" width="${width - 40}" height="${height - 140}" fill="#16213e" stroke="#00d4ff" stroke-width="2" rx="8"/>

        <!-- ÿπŸÜŸàÿßŸÜ -->
        <text x="40" y="100" font-family="Arial" font-size="24" fill="#00d4ff" font-weight="bold">
          ŸÜÿ∏ÿßŸÖ ÿ¨Ÿà - ÿπÿ±ÿ∂ ÿ≥ÿ∑ÿ≠ ÿßŸÑŸÖŸÉÿ™ÿ® ÿßŸÑÿ≠Ÿä
        </text>

        <!-- ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ -->
        <text x="40" y="150" font-family="Arial" font-size="14" fill="#00ff88">
          ÿßŸÑÿ≠ÿßŸÑÿ©: ${this.isStreaming ? 'ŸÇŸäÿØ ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ' : 'ŸÖÿ™ŸàŸÇŸÅ'}
        </text>
        <text x="40" y="180" font-family="Arial" font-size="14" fill="#00ff88">
          ÿßŸÑÿ•ÿ∑ÿßÿ±ÿßÿ™: ${this.stats.totalFrames}
        </text>
        <text x="40" y="210" font-family="Arial" font-size="14" fill="#00ff88">
          ŸÖÿπÿØŸÑ ÿßŸÑÿ•ÿ∑ÿßÿ±ÿßÿ™: ${this.stats.fps.toFixed(1)} FPS
        </text>
        <text x="40" y="240" font-family="Arial" font-size="14" fill="#00ff88">
          ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä: ${this.formatUptime(this.stats.uptime)}
        </text>

        <!-- ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑŸÖŸáÿßŸÖ ÿßŸÑÿ≥ŸÅŸÑŸä -->
        <rect y="${height - 50}" width="${width}" height="50" fill="#0f3460" opacity="0.8"/>
        <text x="20" y="${height - 20}" font-family="Arial" font-size="12" fill="#00d4ff">
          Jo AI System v1.0 | Powered by Manus
        </text>
      </svg>
    `;

    return Buffer.from(svg, 'utf-8');
  }

  /**
   * ÿ®ÿ´ ÿßŸÑÿ•ÿ∑ÿßÿ± ÿ•ŸÑŸâ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖÿ¥ÿ™ÿ±ŸÉŸäŸÜ
   */
  broadcastFrame(frame) {
    if (this.subscribers.size === 0) return;

    const frameData = {
      timestamp: Date.now(),
      size: frame.length,
      data: frame.toString('base64'),
      stats: { ...this.stats }
    };

    this.subscribers.forEach(subscriber => {
      try {
        subscriber(frameData);
      } catch (error) {
        console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ•ÿ∑ÿßÿ±:', error);
        this.subscribers.delete(subscriber);
      }
    });

    this.stats.bandwidth += frame.length;
  }

  /**
   * ÿßŸÑÿßÿ¥ÿ™ÿ±ÿßŸÉ ŸÅŸä ÿßŸÑÿ®ÿ´ ÿßŸÑÿ≠Ÿä
   */
  subscribe(callback) {
    this.subscribers.add(callback);

    // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ•ÿ∑ÿßÿ± ÿßŸÑÿ≠ÿßŸÑŸä ŸÅŸàÿ±ÿßŸã
    if (this.currentScreen) {
      const frameData = {
        timestamp: Date.now(),
        size: this.currentScreen.length,
        data: this.currentScreen.toString('base64'),
        stats: { ...this.stats }
      };
      callback(frameData);
    }

    return () => this.subscribers.delete(callback);
  }

  /**
   * ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿßÿ¥ÿ™ÿ±ÿßŸÉ
   */
  unsubscribe(callback) {
    this.subscribers.delete(callback);
  }

  /**
   * ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
   */
  updateStats() {
    if (this.stats.startTime) {
      this.stats.uptime = Date.now() - this.stats.startTime;
      // ÿ≠ŸÖÿßŸäÿ© ŸÖŸÜ ÿßŸÑŸÇÿ≥ŸÖÿ© ÿπŸÑŸâ ÿµŸÅÿ±
      if (this.stats.uptime > 0) {
        this.stats.fps = (this.stats.totalFrames * 1000) / this.stats.uptime;
      } else {
        this.stats.fps = 0;
      }
    }
  }

  /**
   * ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä
   */
  formatUptime(ms) {
    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    const pad = (n) => n.toString().padStart(2, '0');
    return `${pad(hours)}:${pad(minutes)}:${pad(seconds)}`;
  }

  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿ©
   */
  getStats() {
    return {
      ...this.stats,
      isStreaming: this.isStreaming,
      subscribers: this.subscribers.size,
      uptime: this.formatUptime(this.stats.uptime)
    };
  }

  /**
   * ÿ™ÿπŸäŸäŸÜ ŸÖÿπÿØŸÑ ÿßŸÑÿ•ÿ∑ÿßÿ±ÿßÿ™
   */
  setFrameRate(fps) {
    this.frameRate = Math.max(1, Math.min(30, fps));
    this.frameInterval = 1000 / this.frameRate;
  }

  /**
   * ÿ™ÿπŸäŸäŸÜ ÿ¨ŸàÿØÿ© ÿßŸÑÿµŸàÿ±ÿ©
   */
  setQuality(quality) {
    this.quality = Math.max(1, Math.min(100, quality));
  }

  /**
   * ÿ™ÿπŸäŸäŸÜ ÿØŸÇÿ© ÿßŸÑÿ¥ÿßÿ¥ÿ©
   */
  setResolution(width, height) {
    this.width = width;
    this.height = height;
  }
}

