import { getDB } from '../../core/database.mjs';
import { EventEmitter } from 'events';

class MemoryManager extends EventEmitter {
    constructor(options = {}) {
        super();

        // DB is no longer fetched here. It will be fetched on-demand.
        this.db = null; 

        this.shortTermMemory = new Map();
        this.conversations = new Map();

        this.config = {
            shortTermMemoryTTL: options.shortTermMemoryTTL || 30 * 60 * 1000, 
            cleanupInterval: options.cleanupInterval || 5 * 60 * 1000,
        };

        this.stats = {};

        this.startAutoCleanup();
        console.log('‚úÖ Memory Manager v4.3.0 initialized. DB will be fetched on-demand.');
    }

    // Private helper to get DB connection, ensuring it's initialized.
    _getDB() {
        if (!this.db) {
            this.db = getDB(); // This will throw an error if not initialized, which is correct.
        }
        return this.db;
    }

    startAutoCleanup() {
        console.log(`üßπ Auto-cleanup scheduled every ${this.config.cleanupInterval / 1000 / 60} minutes.`);
        setInterval(() => this.performCleanup(), this.config.cleanupInterval);
    }

    performCleanup() {
        const now = Date.now();
        let cleanedCount = 0;
        // No DB interaction here, so no change needed.
        // ... (cleanup logic remains the same)
    }

    async saveInteraction(userId, command, result, metadata = {}) {
        try {
            const db = this._getDB();
            const interaction = {
                userId,
                command,
                result,
                metadata: {
                    timestamp: new Date(),
                    ...metadata
                },
            };

            await db.collection('joe_interactions').insertOne(interaction);

            this.addToShortTermMemory(userId, interaction);
            this.addToConversationMemory(userId, interaction);

            return { success: true, interactionId: interaction._id };

        } catch (error) {
            console.error('‚ùå Save interaction error:', error);
            return { success: false, error: error.message };
        }
    }

    async getConversationContext(userId, { limit = 15 } = {}) {
        const cachedConversation = this.conversations.get(userId);
        if (cachedConversation && cachedConversation.length > 0) {
            return cachedConversation.slice(-limit);
        }

        try {
            const db = this._getDB();
            const interactions = await db.collection('joe_interactions')
                .find({ userId })
                .sort({ 'metadata.timestamp': -1 })
                .limit(limit)
                .toArray();

            this.conversations.set(userId, interactions.slice().reverse());
            return interactions;
        } catch (error) {
            console.error('‚ùå Get conversation context error:', error);
            return [];
        }
    }

    addToShortTermMemory(userId, interaction) {
        const memory = this.shortTermMemory.get(userId) || [];
        memory.push(interaction);
        this.shortTermMemory.set(userId, memory);
    }

    addToConversationMemory(userId, interaction) {
        const history = this.conversations.get(userId) || [];
        history.push(interaction);
        this.conversations.set(userId, history);
    }
}

// Export the class, not an instance.
export default MemoryManager;

import { getPageContent, getBrowser } from '../browserManager.mjs';
import * as cheerio from 'cheerio';


async function browseWebsite({ url }) {
    try {
        // Use the new caching mechanism
        const html = await getPageContent(url);
        const $ = cheerio.load(html);

        // Remove non-essential tags for cleaner content extraction
        $('script, style, nav, footer, header, iframe, noscript, aside, .ad, .advertisement, [role="banner"], [role="contentinfo"]').remove();

        const title = $('title').text().trim() || $('h1').first().text().trim();
        const description = $('meta[name="description"]').attr('content') ||
                           $('meta[property="og:description"]').attr('content') || '';

        // Intelligent content extraction
        let mainContent = $('article').first().html() || $('main').first().html();
        if (!mainContent) {
            mainContent = $('body').html();
        }

        // Convert HTML to a more readable text format
        const cleanContent = cheerio.load(mainContent, { decodeEntities: true })
            .text()
            .trim()
            .replace(/\s{2,}/g, ' \n'); // Normalize whitespace

        // Extract key links
        const links = [];
        $('a[href]').each((i, elem) => {
            const href = $(elem).attr('href');
            const text = $(elem).text().trim();
            if (href && (href.startsWith('http') || href.startsWith('/')) && text && links.length < 15) {
                try {
                    const absoluteUrl = new URL(href, url).href;
                    if (!links.some(l => l.url === absoluteUrl)) {
                       links.push({ text, url: absoluteUrl });
                    }
                } catch(e) { /* Ignore invalid URLs */ }
            }
        });

        return {
            success: true,
            url,
            title,
            description,
            content: cleanContent.substring(0, 12000), // Increased content limit
            links
        };
    } catch (error) {
        console.error(`‚ùå Error in browseWebsite for ${url}:`, error);
        return { success: false, error: `Failed to browse website: ${error.message}` };
    }
}
browseWebsite.metadata = {
    name: "browseWebsite",
    description: "Fetches, parses, and summarizes the content of a specific URL by rendering it in a real browser. Use this to read articles, get details from dynamic webpages, or analyze a site's content.",
    parameters: {
        type: "object",
        properties: {
            url: { type: "string", description: "The full URL of the website to browse." }
        },
        required: ["url"]
    }
};

async function screenshotWebsite({ url, fullPage = false }) {
    const browser = await getBrowser();
    const page = await browser.newPage({ deviceScaleFactor: 2 }); // Higher resolution
    try {
        await page.setViewportSize({ width: 1280, height: 800 });
        await page.goto(url, { waitUntil: 'networkidle', timeout: 25000 });

        const screenshotBuffer = await page.screenshot({ 
            path: `screenshot-${new Date().toISOString()}.png`,
            fullPage,
            type: 'png'
        });

        return { 
            success: true, 
            message: "Screenshot saved successfully.",
            path: `screenshot-${new Date().toISOString()}.png`
        };

    } catch (error) {
        console.error(`‚ùå Error taking screenshot for ${url}:`, error);
        return { success: false, error: `Failed to take screenshot: ${error.message}` };
    } finally {
        await page.close();
    }
}
