/**
 * üîç Intelligent Code Review System
 * Professional code review, like working with a Senior Developer.
 */

import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY
});

class IntelligentCodeReviewSystem {

  async reviewCode(code, language, context = {}) {
    console.log(`üîç Starting code review for ${language}...`);

    const reviews = await Promise.all([
      this.securityReview(code, language),
      this.performanceReview(code, language),
      this.qualityReview(code, language),
    ]);

    const issues = this.aggregateIssues(reviews);
    const score = this.calculateScore(issues);
    const suggestions = await this.generateSuggestions(code, issues);
    const improvedCode = await this.improveCode(code, suggestions);

    return {
      score,
      grade: this.getGrade(score),
      issues,
      suggestions,
      improvedCode,
      reviews,
      report: await this.generateReport(reviews, score)
    };
  }

  async securityReview(code, language) {
    const prompt = `
    Analyze the following ${language} code for security vulnerabilities:

    \`\`\`${language}
    ${code}
    \`\`\`

    Identify vulnerabilities like SQL Injection, XSS, Command Injection, Hardcoded Secrets, etc. Return a JSON array of issues: 
    [{"type": "SQL Injection", "severity": "critical", "line": 10, "description": "...", "fix": "..."}]
    `;
    const response = await openai.chat.completions.create({
        model: 'gpt-4o',
        messages: [{ role: 'system', content: `You are a ${language} security expert.` }, { role: 'user', content: prompt }],
        response_format: { type: 'json_object' }
    });
    const vulnerabilities = JSON.parse(response.choices[0].message.content).vulnerabilities || [];
    return {
      reviewer: 'security',
      vulnerabilities,
      score: Math.max(0, 100 - vulnerabilities.length * 15),
    };
  }

  async performanceReview(code, language) {
    const prompt = `
    Analyze the following ${language} code for performance issues:

    \`\`\`${language}
    ${code}
    \`\`\`

    Identify issues like nested loops, memory inefficiencies, and algorithmic bottlenecks. Return a JSON array of issues:
    [{"type": "Nested Loops", "severity": "medium", "description": "...", "fix": "..."}]
    `;
    const response = await openai.chat.completions.create({
        model: 'gpt-4o',
        messages: [{ role: 'system', content: `You are a ${language} performance expert.` }, { role: 'user', content: prompt }],
        response_format: { type: 'json_object' }
    });
    const issues = JSON.parse(response.choices[0].message.content).issues || [];
    return {
      reviewer: 'performance',
      issues,
      score: Math.max(0, 100 - issues.length * 10),
    };
  }

  async qualityReview(code, language) {
    const prompt = `
    Analyze the following ${language} code for quality issues:

    \`\`\`${language}
    ${code}
    \`\`\`

    Identify issues like poor naming, long functions, and lack of comments. Return a JSON array of issues:
    [{"type": "Poor Naming", "severity": "low", "description": "...", "fix": "..."}]
    `;
    const response = await openai.chat.completions.create({
        model: 'gpt-4o',
        messages: [{ role: 'system', content: 'You are a clean code advocate.' }, { role: 'user', content: prompt }],
        response_format: { type: 'json_object' }
    });
    const issues = JSON.parse(response.choices[0].message.content).issues || [];
    return {
      reviewer: 'quality',
      issues,
      score: Math.max(0, 100 - issues.length * 8),
    };
  }

  aggregateIssues(reviews) {
    let allIssues = [];
    reviews.forEach(review => {
      if (review.vulnerabilities) allIssues = allIssues.concat(review.vulnerabilities);
      if (review.issues) allIssues = allIssues.concat(review.issues);
    });
    return allIssues;
  }

  calculateScore(issues) {
    let score = 100;
    for (const issue of issues) {
      switch (issue.severity) {
        case 'critical': score -= 20; break;
        case 'high': score -= 15; break;
        case 'medium': score -= 10; break;
        case 'low': score -= 5; break;
      }
    }
    return Math.max(0, score);
  }

  getGrade(score) {
    if (score >= 90) return 'A+';
    if (score >= 80) return 'A';
    if (score >= 70) return 'B';
    if (score >= 60) return 'C';
    return 'F';
  }

  async generateSuggestions(code, issues) {
    return issues.map(issue => ({ description: issue.description, fix: issue.fix, line: issue.line }));
  }

  async improveCode(code, suggestions) {
    const prompt = `
    Improve this code based on the following suggestions:

    \`\`\`
    ${code}
    \`\`\`

    Suggestions:
    ${suggestions.map(s => `- (Line ${s.line}) ${s.description}: ${s.fix}`).join('
')}

    Return only the improved code block.
    `;
    const response = await openai.chat.completions.create({
      model: 'gpt-4o',
      messages: [{ role: 'system', content: 'You are an expert developer tasked with refactoring code.' }, { role: 'user', content: prompt }],
      temperature: 0.3
    });
    let improved = response.choices[0].message.content;
    const match = improved.match(/```(?:[a-z]+)?
([\s\S]*?)```/);
    return match ? match[1] : improved;
  }

  async generateReport(reviews, score) {
    const prompt = `
    Write a professional code review report based on the following data:

    Overall Score: ${score}/100
    Reviews: ${JSON.stringify(reviews, null, 2)}

    The report should include an executive summary, strengths, weaknesses, and a prioritized action plan.
    `;
    const response = await openai.chat.completions.create({
      model: 'gpt-4o',
      messages: [{ role: 'system', content: 'You are a Senior Code Reviewer.' }, { role: 'user', content: prompt }]
    });
    return response.choices[0].message.content;
  }
}

