import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Play, Copy, Trash2, Loader2, Wifi, WifiOff, RefreshCcw } from 'lucide-react';

// ÿßŸÅÿ™ÿ±ÿßÿ∂ ÿ£ŸÜ Ÿáÿ∞Ÿá ÿßŸÑÿØŸàÿßŸÑ ŸÖŸàÿ¨ŸàÿØÿ© ŸÅŸä ŸÖŸÑŸÅÿßÿ™ utils/websocket.ts Ÿà utils/api.ts
// ŸàŸäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ ŸÖÿ™ŸàÿßŸÅŸÇÿ© ŸÖÿπ ÿßŸÑÿ™ŸàŸÇŸäÿπÿßÿ™ ÿßŸÑÿ™ÿßŸÑŸäÿ©:

/**
 * Ÿäÿ™ÿµŸÑ ÿ®ŸÄ WebSocket.
 * @param {(message: any) => void} onMessage - ÿØÿßŸÑÿ© ÿ™Ÿèÿ≥ÿ™ÿØÿπŸâ ÿπŸÜÿØ ÿßÿ≥ÿ™ŸÇÿ®ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ©.
 * @param {() => void} onOpen - ÿØÿßŸÑÿ© ÿ™Ÿèÿ≥ÿ™ÿØÿπŸâ ÿπŸÜÿØ ŸÅÿ™ÿ≠ ÿßŸÑÿßÿ™ÿµÿßŸÑ.
 * @param {() => void} onClose - ÿØÿßŸÑÿ© ÿ™Ÿèÿ≥ÿ™ÿØÿπŸâ ÿπŸÜÿØ ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿßÿ™ÿµÿßŸÑ.
 * @returns {WebSocket | null} ŸÖÿ´ŸäŸÑ WebSocket ÿßŸÑŸÖÿ™ÿµŸÑ.
 */
const connectWebSocket = (onMessage, onOpen, onClose) => {
  // Ÿäÿ¨ÿ® ÿπŸÑŸäŸÉ ÿ™ÿπÿØŸäŸÑ Ÿáÿ∞ÿß ÿßŸÑÿ¨ÿ≤ÿ° ŸÑŸäÿ™ŸÜÿßÿ≥ÿ® ŸÖÿπ ÿ∑ÿ±ŸäŸÇÿ© ÿßÿ™ÿµÿßŸÑŸÉ ÿ®ŸÄ WebSocket
  // ŸÖÿ´ÿßŸÑ:
  const ws = new WebSocket('ws://localhost:8000/ws'); // ÿßÿ≥ÿ™ÿ®ÿØŸÑ ÿ®ÿßŸÑŸÄ URL ÿßŸÑÿÆÿßÿµ ÿ®ŸÉ
  ws.onopen = onOpen;
  ws.onmessage = (event) => {
    try {
      const data = JSON.parse(event.data);
      onMessage(data);
    } catch (e) {
      onMessage(event.data); // ŸÅŸä ÿ≠ÿßŸÑ ŸÉÿßŸÜÿ™ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ŸÜÿµŸãÿß ÿÆÿßŸÖŸãÿß
    }
  };
  ws.onclose = onClose;
  ws.onerror = (error) => {
    console.error('WebSocket Error:', error);
    onClose(); // ÿßÿ≥ÿ™ÿØÿπÿßÿ° onClose ÿπŸÜÿØ ÿ≠ÿØŸàÿ´ ÿÆÿ∑ÿ£ ÿ£Ÿäÿ∂Ÿãÿß
  };
  return ws;
};

/**
 * ŸäŸÇÿ∑ÿπ ÿßÿ™ÿµÿßŸÑ WebSocket.
 * @param {WebSocket | null} ws - ŸÖÿ´ŸäŸÑ WebSocket ŸÑŸÇÿ∑ÿπŸá.
 */
const disconnectWebSocket = (ws) => {
  if (ws && ws.readyState === WebSocket.OPEN) {
    ws.close();
  }
};

/**
 * Ÿäÿ±ÿ≥ŸÑ ÿ∑ŸÑÿ® API.
 * @param {string} url - ŸÖÿ≥ÿßÿ± API.
 * @param {RequestInit} options - ÿÆŸäÿßÿ±ÿßÿ™ ÿ∑ŸÑÿ® Fetch.
 * @returns {Promise<any>} ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© API.
 */
const apiRequest = async (url, options) => {
  // Ÿäÿ¨ÿ® ÿπŸÑŸäŸÉ ÿ™ÿπÿØŸäŸÑ Ÿáÿ∞ÿß ÿßŸÑÿ¨ÿ≤ÿ° ŸÑŸäÿ™ŸÜÿßÿ≥ÿ® ŸÖÿπ ÿ∑ÿ±ŸäŸÇÿ© ÿ∑ŸÑÿ®ŸÉ ŸÑŸÄ API
  // ŸÖÿ´ÿßŸÑ:
  const response = await fetch(url, options);
  if (!response.ok) {
    const errorData = await response.json().catch(() => ({ message: response.statusText }));
    throw new Error(errorData.message || 'ŸÅÿ¥ŸÑ ÿ∑ŸÑÿ® API');
  }
  return response.json();
};


