    if (!persistOutput) return;
    const saved = localStorage.getItem('agent_panel_output');
    if (saved) setOutput(saved);
  }, [persistOutput]);

  useEffect(() => {
    if (!persistOutput) return;
    localStorage.setItem('agent_panel_output', output);
  }, [output, persistOutput]);

  // ÿ™ŸÖÿ±Ÿäÿ± ÿ™ŸÑŸÇÿßÿ¶Ÿä ŸÑÿ£ÿ≥ŸÅŸÑ ŸÖÿπ ŸÉŸÑ ÿ™ÿ≠ÿØŸäÿ´
  useEffect(() => {
    const el = outputRef.current;
    if (el) el.scrollTop = el.scrollHeight;
  }, [output]);

  // ÿ™ŸàÿµŸäŸÑ WebSocket ŸÖÿπ ÿ•ÿπÿßÿØÿ© ÿßÿ™ÿµÿßŸÑ ÿßÿÆÿ™Ÿäÿßÿ±Ÿäÿ©
  const connectWS = useCallback(() => {
    // ÿ•ÿ∫ŸÑÿßŸÇ ÿ£Ÿä ÿßÿ™ÿµÿßŸÑ ÿ≥ÿßÿ®ŸÇ ŸÇÿ®ŸÑ ÿ•ŸÜÿ¥ÿßÿ° ÿßÿ™ÿµÿßŸÑ ÿ¨ÿØŸäÿØ
    disconnectWebSocket(wsRef.current);

    const ws = connectWebSocket(
      (message) => {
        // ÿ±ÿ≥ÿßÿ¶ŸÑ WebSocket ŸÇÿØ ÿ™ŸÉŸàŸÜ ŸÜÿµŸãÿß ÿÆÿßŸÖŸãÿß ÿ£Ÿà JSON
        if (typeof message === 'object' && message?.output) {
          setOutput((prev) => (prev ? prev + '\n' : '') + String(message.output));
        } else if (typeof message === 'string') {
          setOutput((prev) => (prev ? prev + '\n' : '') + message);
        }
      },
      () => {
        setWsConnected(true);
        setWsAttempt(0); // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ŸÖÿ≠ÿßŸàŸÑÿßÿ™ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿπŸÜÿØ ÿßŸÑŸÜÿ¨ÿßÿ≠
        console.log('WebSocket connected');
      },
      () => {
        setWsConnected(false);
        console.log('WebSocket disconnected');
        // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä
        if (autoReconnect) {
          const delay = Math.min(10000, 1000 * (wsAttempt + 1)); // ÿ≤ŸäÿßÿØÿ© ÿ™ÿØÿ±Ÿäÿ¨Ÿäÿ© ŸÅŸä ÿßŸÑÿ™ÿ£ÿÆŸäÿ±
          setTimeout(() => setWsAttempt((n) => n + 1), delay);
        }
      }
    );
    wsRef.current = ws; // ÿ™ÿÆÿ≤ŸäŸÜ ŸÖÿ´ŸäŸÑ WebSocket
  }, [autoReconnect, wsAttempt]);

  // ÿ•ÿØÿßÿ±ÿ© ÿØŸàÿ±ÿ© ÿ≠Ÿäÿßÿ© WebSocket
  useEffect(() => {
    connectWS();
    return () => {
      disconnectWebSocket(wsRef.current);
    };
  }, [connectWS]); // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± connectWS (ÿ®ÿ≥ÿ®ÿ® wsAttempt)

  // ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿ∑ŸÑÿ® ÿ•ŸÑŸâ ÿßŸÑŸÄAPI
  const executeCode = async (payload) => {
    setLoading(true);
    setError(null);
    try {
      const result = await apiRequest(apiPath, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...requestHeaders,
        },
        body: JSON.stringify({ code: payload }),
      });
      // ŸÅŸä ÿ≠ÿßŸÑ API ŸäÿπŸäÿØ output ŸÑÿ≠ÿ∏ŸäÿßŸã
      if (result?.output) {
        setOutput((prev) => (prev ? prev + '\n' : '') + String(result.output));
      } else if (!result?.streaming) { // ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸáŸÜÿßŸÉ ÿ®ÿ´ ŸàŸÑŸÖ ŸäŸÉŸÜ ŸáŸÜÿßŸÉ ÿ•ÿÆÿ±ÿßÿ¨ ŸÅŸàÿ±Ÿä
        setOutput((prev) => (prev ? prev + '\n' : '') + 'ŸÑÿß ŸäŸàÿ¨ÿØ ÿ•ÿÆÿ±ÿßÿ¨ ŸÖÿ®ÿßÿ¥ÿ± ŸÖŸÜ API');
      }
    } catch (err) {
      const msg = err?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ŸÜŸÅŸäÿ∞';
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  // ÿßÿÆÿ™ÿµÿßÿ± ÿ™ŸÜŸÅŸäÿ∞: Ctrl/Cmd + Enter
  useEffect(() => {
    const onKey = (e) => {
      const isMac = /Mac|iPod|iPhone|iPad/.test(navigator.platform);
      if ((isMac ? e.metaKey : e.ctrlKey) && e.key === 'Enter') {
        e.preventDefault();
        if (!loading) executeCode(code);
      }
    };
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, [code, loading, executeCode]); // ÿ•ÿ∂ÿßŸÅÿ© executeCode ŸÑŸÑÿ™ÿ®ÿπŸäÿßÿ™

  // ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ŸÖÿ≥ÿßÿπÿØÿ©
  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(output);
    } catch {
      // ÿ™ÿ¨ÿßŸáŸÑ ÿ®ÿµŸÖÿ™
    }
  };

  const clearOutput = () => setOutput('');

  return (
    <div className="rounded-lg border border-slate-700 bg-gradient-to-b from-slate-900 to-slate-800 text-slate-100 shadow-lg">
      {/* ÿ¥ÿ±Ÿäÿ∑ ÿπŸÑŸàŸä */}
      <div className="flex items-center gap-3 px-4 py-3 border-b border-slate-700/60">
        <div className={`flex items-center gap-1 text-xs px-2 py-1 rounded ${wsConnected ? 'bg-green-500/15 text-green-400' : 'bg-red-500/15 text-red-400'}`}>
          {wsConnected ? <Wifi size={14} /> : <WifiOff size={14} />}
          <span>{wsConnected ? 'ŸÖÿ™ÿµŸÑ' : 'ÿ∫Ÿäÿ± ŸÖÿ™ÿµŸÑ'}</span>
        </div>

        <div className="ml-auto flex items-center gap-2">
          <button
            type="button"
            onClick={() => executeCode(code)}
            disabled={loading}
            className="inline-flex items-center gap-2 px-3 py-1.5 rounded bg-cyan-500/20 text-cyan-300 hover:bg-cyan-500/30 border border-cyan-500/30 disabled:opacity-60"
            title="ÿ™ŸÜŸÅŸäÿ∞ (Ctrl/‚åò + Enter)"
          >
            {loading ? <Loader2 size={16} className="animate-spin" /> : <Play size={16} />}
            <span>ÿ™ŸÜŸÅŸäÿ∞</span>
          </button>

          <button
            type="button"
            onClick={copyToClipboard}
            className="inline-flex items-center gap-2 px-3 py-1.5 rounded bg-slate-700 text-slate-200 hover:bg-slate-600 border border-slate-600"
            title="ŸÜÿ≥ÿÆ ÿßŸÑÿ•ÿÆÿ±ÿßÿ¨"
          >
            <Copy size={16} />
            <span>ŸÜÿ≥ÿÆ</span>
          </button>

          <button
            type="button"
            onClick={clearOutput}
            className="inline-flex items-center gap-2 px-3 py-1.5 rounded bg-slate-700 text-slate-200 hover:bg-slate-600 border border-slate-600"
            title="ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ•ÿÆÿ±ÿßÿ¨"
          >
            <Trash2 size={16} />
            <span>ÿ™ŸÅÿ±Ÿäÿ∫</span>
          </button>

          {!wsConnected && (
            <button
              type="button"
              onClick={() => {
                connectWS(); // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ ŸÖÿ®ÿßÿ¥ÿ±ÿ©
              }}
              className="inline-flex items-center gap-2 px-3 py-1.5 rounded bg-amber-500/20 text-amber-200 hover:bg-amber-500/30 border border-amber-500/30"
              title="ÿ•ÿπÿßÿØÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ"
            >
              <RefreshCcw size={16} />
              <span>ÿ•ÿπÿßÿØÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ</span>
            </button>
          )}
        </div>
      </div>

      {/* ŸÖŸÜÿ∑ŸÇÿ© ÿ•ÿØÿÆÿßŸÑ ÿßŸÑŸÉŸàÿØ */}
      <div className="p-4 space-y-3">
        <label htmlFor="agent-code-input" className="block text-sm text-slate-400">ÿßŸÑÿ™ÿπŸÑŸäŸÖÿßÿ™/ÿßŸÑŸÉŸàÿØ</label>
        <textarea
          id="agent-code-input"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          rows={6}
          dir="auto"
          className="w-full resize-y rounded-md bg-slate-900/60 border border-slate-700 focus:outline-none focus:ring-2 focus:ring-cyan-500/40 focus:border-cyan-500/50 p-3 text-sm"
          placeholder="ÿßŸÉÿ™ÿ® ŸÉŸàÿØÿßŸã ÿ£Ÿà ÿ™ÿπŸÑŸäŸÖÿßÿ™ ŸÑŸäŸÇŸàŸÖ ÿßŸÑŸàŸÉŸäŸÑ ÿ®ÿ™ŸÜŸÅŸäÿ∞Ÿáÿß..."
        />

        {error && (
          <div className="flex items-start gap-2 p-3 rounded-md border border-red-500/30 bg-red-500/10 text-red-300 text-sm">
            <span className="font-semibold">ÿÆÿ∑ÿ£:</span>
            <span className="flex-1">{String(error)}</span>
          </div>
        )}
      </div>

      {/* ÿßŸÑÿ•ÿÆÿ±ÿßÿ¨ */}
      <div className="px-4 pb-4">
        <label className="block text-sm text-slate-400 mb-2">ÿßŸÑŸÖÿÆÿ±ÿ¨ÿßÿ™</label>
        <div
          ref={outputRef}
          className="h-64 overflow-auto rounded-md bg-slate-950 border border-slate-800 p-3 text-xs font-mono whitespace-pre-wrap leading-relaxed"
        >
          {output ? output : <span className="text-slate-500">ŸÑÿß ŸäŸàÿ¨ÿØ ÿ•ÿÆÿ±ÿßÿ¨ ÿ®ÿπÿØ...</span>}
        </div>
      </div>
    </div>
  );
};

export default AgentPanel;

import React, { useState, useEffect, useRef, useCallback } from 'react';
import apiClient from '../api/client'; // Make sure this path is correct
import { Loader2, X, MousePointer2, Keyboard, Globe } from 'lucide-react';

const API_BASE = import.meta.env.VITE_API_BASE || 'https://admin.xelitesolutions.com';

/**
 * Interactive browser viewer that allows full user control.
 * Requires a robust backend (e.g., Node.js + Puppeteer/Playwright + WebSockets).
 * @param {{
 *   sessionId: string, // ID of the browser session to display
 *   onClose: () => void, // Function to call when the viewer is closed
 *   language: 'ar' | 'en' // To change the language
 * }} props
 */
export default function BrowserViewer({ sessionId, onClose, language = 'ar' }) {
  const [screenshot, setScreenshot] = useState(null);
  const [url, setUrl] = useState('');
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [isUserControlled, setIsUserControlled] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const intervalRef = useRef(null);
  const canvasRef = useRef(null);
  const browserScreenRef = useRef(null);

  const texts = {
    ar: {
      title: "ŸÖÿ™ÿµŸÅÿ≠ ÿ™ŸÅÿßÿπŸÑŸä",
      loading: "ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ™ÿµŸÅÿ≠...",
      loadingWait: "Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿßŸÜÿ™ÿ∏ÿßÿ±.",
      youControl: "ÿ£ŸÜÿ™ ÿ™ÿ™ÿ≠ŸÉŸÖ",
      joeControls: "JOE Ÿäÿ™ÿ≠ŸÉŸÖ",
      toggleControl: "ÿ™ÿ®ÿØŸäŸÑ ÿßŸÑÿ™ÿ≠ŸÉŸÖ",
      closeViewer: "ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿπÿßÿ±ÿ∂",
      live: "ŸÖÿ®ÿßÿ¥ÿ±",
      mouse: "ÿßŸÑŸÖÿßŸàÿ≥",
      errorPrefix: "ÿÆÿ∑ÿ£",
      failedFetchScreenshot: "ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ŸÑŸÇÿ∑ÿ© ÿßŸÑÿ¥ÿßÿ¥ÿ©.",
      failedToggleControl: "ŸÅÿ¥ŸÑ ÿ™ÿ®ÿØŸäŸÑ ÿßŸÑÿ™ÿ≠ŸÉŸÖ.",
      failedCloseSession: "ŸÅÿ¥ŸÑ ÿ•ÿ∫ŸÑÿßŸÇ ÿ¨ŸÑÿ≥ÿ© ÿßŸÑŸÖÿ™ÿµŸÅÿ≠ ÿπŸÑŸâ ÿßŸÑÿÆÿßÿØŸÖ.",
      instructionYouControl: "üí° ÿ£ŸÜÿ™ ÿ™ÿ™ÿ≠ŸÉŸÖ: ÿßŸÜŸÇÿ±ÿå ÿßŸÉÿ™ÿ®ÿå Ÿàÿ™ÿµŸÅÿ≠ ŸÉÿßŸÑŸÖÿπÿ™ÿßÿØ!",
      instructionJoeControl: "üí° ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ 'ÿ£ŸÜÿ™ ÿ™ÿ™ÿ≠ŸÉŸÖ' ŸÑÿ£ÿÆÿ∞ ÿßŸÑÿ≥Ÿäÿ∑ÿ±ÿ© ÿπŸÑŸâ ÿßŸÑŸÖÿ™ÿµŸÅÿ≠.",
      notAuthorized: "ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ÿ®Ÿá ÿ£Ÿà ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©.",
      failedProcessEvent: "ŸÅÿ¥ŸÑ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ≠ÿØÿ´.",
      sessionNotFound: "ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©.",
    },
    en: {
      title: "Interactive Browser",
      loading: "Loading browser...",
      loadingWait: "Please wait.",
      youControl: "You Control",
      joeControls: "JOE Controls",
      toggleControl: "Toggle Control",
      closeViewer: "Close Viewer",
      live: "Live",
      mouse: "Mouse",
      errorPrefix: "Error",
      failedFetchScreenshot: "Failed to fetch screenshot.",
      failedToggleControl: "Failed to toggle control.",
      failedCloseSession: "Failed to close browser session on server.",
      instructionYouControl: "üí° You are in control: Click, type, and browse as usual!",
      instructionJoeControl: "üí° Click 'You Control' to take over the browser.",
      notAuthorized: "Not authorized or session not found.",
      failedProcessEvent: "Failed to process event.",
      sessionNotFound: "Session not found.",
    }
  };

  const t = texts[language];

  const sendBrowserEvent = useCallback(async (eventType, payload) => {
    if (!sessionId || !isUserControlled) return;
    try {
      await apiClient.post(`${API_BASE}/api/browser/event`, {
        sessionId,
        eventType,
        payload,
      });
    } catch (err) {
      console.error(`Failed to send browser event (${eventType}):`, err);
      setError(`${t.errorPrefix}: ${err.response?.data?.message || err.message || 'Unknown error'}`);
    }
  }, [sessionId, isUserControlled, t.errorPrefix]);

  const fetchScreenshot = useCallback(async () => {
    if (!sessionId) return;
    try {
      const response = await apiClient.post(`${API_BASE}/api/browser/screenshot`, {
        sessionId
      });
      if (response.data.ok) {
        setScreenshot(response.data.screenshot);
        setUrl(response.data.url);
        setMousePos(response.data.mousePosition || { x: 0, y: 0 });
        setError(null);
      } else {
        setError(response.data.message || t.failedFetchScreenshot);
      }
    } catch (err) {
      console.error('BrowserViewer: Screenshot fetch error:', err);
      setError(`${t.errorPrefix}: ${t.failedFetchScreenshot}`);
    }
  }, [sessionId, t.errorPrefix, t.failedFetchScreenshot]);

  useEffect(() => {
    fetchScreenshot();
    intervalRef.current = setInterval(fetchScreenshot, 200);
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [fetchScreenshot]);

  useEffect(() => {
    if (!canvasRef.current || !screenshot) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.onload = () => {
      const parent = browserScreenRef.current;
      if (!parent) return;
      const aspectRatio = img.width / img.height;
      let newWidth = parent.clientWidth;
      let newHeight = newWidth / aspectRatio;
      if (newHeight > parent.clientHeight) {
        newHeight = parent.clientHeight;
        newWidth = newHeight * aspectRatio;
      }
      canvas.width = newWidth;
      canvas.height = newHeight;
      ctx?.drawImage(img, 0, 0, newWidth, newHeight);
      if (ctx) {
        const scaleX = newWidth / img.width;
        const scaleY = newHeight / img.height;
        ctx.fillStyle = 'rgba(255, 0, 0, 0.7)';
        ctx.strokeStyle = 'white';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(mousePos.x * scaleX, mousePos.y * scaleY, 8, 0, 2 * Math.PI);
        ctx.fill();
        ctx.stroke();
      }
    };
    img.src = screenshot;
  }, [screenshot, mousePos]);

  const toggleControl = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await apiClient.post(`${API_BASE}/api/browser/toggle-control`, {
        sessionId,
        userControlled: !isUserControlled
      });
      if (response.data.ok) {
        setIsUserControlled(response.data.isUserControlled);
      } else {
        setError(response.data.message || t.failedToggleControl);
      }
    } catch (err) {
      console.error('BrowserViewer: Toggle control error:', err);
      setError(`${t.errorPrefix}: ${t.failedToggleControl}`);
    } finally {
      setIsLoading(false);
    }
  }, [sessionId, isUserControlled, t.errorPrefix, t.failedToggleControl]);

  const handleClose = useCallback(async () => {
    setError(null);
    try {
      await apiClient.post(`${API_BASE}/api/browser/close`, { sessionId });
    } catch (err) {
      console.error('BrowserViewer: Close error:', err);
      setError(`${t.errorPrefix}: ${t.failedCloseSession}`);
    }
    onClose();
  }, [sessionId, onClose, t.errorPrefix, t.failedCloseSession]);

  const handleMouseMove = useCallback((e) => {
    if (!isUserControlled || !canvasRef.current || !screenshot) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const img = new Image();
    img.src = screenshot;
    const scaleX = img.width / rect.width;
    const scaleY = img.height / rect.height;
    const x = (e.clientX - rect.left) * scaleX;
    const y = (e.clientY - rect.top) * scaleY;
    setMousePos({ x, y });
    sendBrowserEvent('mousemove', { x, y });
  }, [isUserControlled, sendBrowserEvent, screenshot]);

  const handleClick = useCallback((e) => {
    if (!isUserControlled || !canvasRef.current || !screenshot) return;
    const rect = canvasRef.current.getBoundingClientRect();
    const img = new Image();
    img.src = screenshot;
    const scaleX = img.width / rect.width;
    const scaleY = img.height / rect.height;
    const x = (e.clientX - rect.left) * scaleX;
    const y = (e.clientY - rect.top) * scaleY;
    sendBrowserEvent('click', { x, y, button: e.button });
  }, [isUserControlled, sendBrowserEvent, screenshot]);

  const handleScroll = useCallback((e) => {
    if (!isUserControlled) return;
    sendBrowserEvent('scroll', { deltaY: e.deltaY });
  }, [isUserControlled, sendBrowserEvent]);

  const handleKeyDown = useCallback((e) => {
    if (!isUserControlled) return;
    sendBrowserEvent('keydown', { key: e.key, code: e.code, ctrlKey: e.ctrlKey, shiftKey: e.shiftKey, altKey: e.altKey, metaKey: e.metaKey });
    e.preventDefault();
  }, [isUserControlled, sendBrowserEvent]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas && isUserControlled) {
      canvas.addEventListener('mousemove', handleMouseMove);
      canvas.addEventListener('click', handleClick);
      canvas.addEventListener('wheel', handleScroll);
      window.addEventListener('keydown', handleKeyDown);
    }
    return () => {
      if (canvas) {
        canvas.removeEventListener('mousemove', handleMouseMove);
        canvas.removeEventListener('click', handleClick);
        canvas.removeEventListener('wheel', handleScroll);
      }
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [isUserControlled, handleMouseMove, handleClick, handleScroll, handleKeyDown]);

  return (
    <div className={`fixed inset-0 bg-gray-900/95 z-50 flex flex-col font-sans text-white ${language === 'ar' ? 'rtl' : 'ltr'}`}>
      <div className="bg-gray-800 border-b border-gray-700 p-4 flex items-center justify-between shadow-lg">
        <div className="flex items-center gap-4">
          <h3 className="text-2xl font-extrabold text-teal-400 flex items-center gap-2">
            <Globe className="w-6 h-6" /> {t.title}
          </h3>
          <div className="text-sm text-gray-400 truncate max-w-md">
            {url || t.loading}
          </div>
        </div>
        <div className="flex items-center gap-3">
          <button
            onClick={toggleControl}
            disabled={isLoading}
            className={`px-5 py-2 rounded-full font-semibold transition-all duration-300 flex items-center gap-2 ${isUserControlled ? 'bg-green-600 hover:bg-green-700 text-white shadow-md' : 'bg-blue-600 hover:bg-blue-700 text-white shadow-md'} ${isLoading ? 'opacity-70 cursor-not-allowed' : ''}`}>
            {isLoading && <Loader2 className="h-5 w-5 animate-spin" />}
            {isUserControlled ? t.youControl : t.joeControls}
          </button>
          <button onClick={handleClose} className="text-gray-400 hover:text-white text-3xl px-2 leading-none transition-colors duration-200" title={t.closeViewer}>
            <X className="w-7 h-7" />
          </button>
        </div>
      </div>
      <div ref={browserScreenRef} className="flex-1 overflow-hidden bg-gray-900 p-6 flex items-center justify-center relative">
        {error && (
          <div className={`absolute top-6 ${language === 'ar' ? 'left-1/2 -translate-x-1/2' : 'right-6'} bg-red-600 text-white px-5 py-3 rounded-lg shadow-xl z-10 animate-fade-in text-lg font-medium`}>
            {error}
          </div>
        )}
        {screenshot ? (
          <div className="relative max-w-full max-h-full flex items-center justify-center">
            <canvas ref={canvasRef} className={`border-2 border-gray-700 rounded-xl shadow-2xl transition-all duration-100 ${isUserControlled ? 'cursor-none' : 'cursor-default'}`} style={{ maxWidth: '100%', maxHeight: '100%' }} tabIndex={isUserControlled ? 0 : -1} />
            {isUserControlled && (
              <div className={`absolute top-6 ${language === 'ar' ? 'right-6' : 'left-6'} bg-green-500 text-white px-5 py-2 rounded-lg font-bold shadow-lg animate-pulse z-10 flex items-center gap-2`}>
                <MousePointer2 className="w-5 h-5" /> {t.youControl}
              </div>
            )}
          </div>
        ) : (
          <div className="text-center text-gray-400">
            <Loader2 className="text-teal-400 w-16 h-16 mb-4 animate-spin" />
            <p className="text-xl font-medium">{t.loading}</p>
            <p className="text-md">{t.loadingWait}</p>
          </div>
        )}
      </div>
      <div className="bg-gray-800 border-t border-gray-700 p-4 flex items-center justify-between text-sm text-gray-400 shadow-inner">
        <div className="flex items-center gap-6">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
            <span>{t.live}</span>
          </div>
          <div>
            {t.mouse}: ({Math.round(mousePos.x)}, {Math.round(mousePos.y)})
          </div>
        </div>
        <div className="flex items-center gap-2 text-base font-medium text-gray-300">
          {isUserControlled
            ? <><Keyboard className="w-5 h-5" /> {t.instructionYouControl}</>
            : <><MousePointer2 className="w-5 h-5" /> {t.instructionJoeControl}</>
          }
        </div>
      </div>
    </div>
  );
}

