import React, { useState, useEffect, useCallback } from 'react';
import { Plus, Trash2, MessageSquare, Loader2, ChevronLeft, ChevronRight, History } from 'lucide-react'; // ÿ£ŸäŸÇŸàŸÜÿßÿ™ Lucide
import apiClient from '../api/client'; // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßŸÑŸÖÿ≥ÿßÿ± ÿßŸÑÿµÿ≠Ÿäÿ≠ ŸÑŸÄ apiClient

const API_BASE = import.meta.env.VITE_API_BASE || 'https://admin.xelitesolutions.com';

export default function ChatSidebar({
  userId,
  currentConversationId,
  onSelectConversation,
  onNewConversation,
  onCollapse // ÿßŸÑÿ¢ŸÜ Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖŸáÿß
}) {
  const [conversations, setConversations] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false); // ÿ≠ÿßŸÑÿ© ÿØÿßÿÆŸÑŸäÿ© ŸÑŸÑÿ∑Ÿä

  // ÿØÿßŸÑÿ© ŸÑÿ™ÿ®ÿØŸäŸÑ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ∑Ÿä
  const toggleCollapse = () => {
    setIsCollapsed(prev => !prev);
    if (onCollapse) {
      onCollapse(!isCollapsed); // ÿ•ÿ®ŸÑÿßÿ∫ ÿßŸÑŸÖŸÉŸàŸÜ ÿßŸÑÿ£ÿ® ÿ®ÿ≠ÿßŸÑÿ© ÿßŸÑÿ∑Ÿä ÿßŸÑÿ¨ÿØŸäÿØÿ©
    }
  };

  useEffect(() => {
    if (userId) {
      loadConversations();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  const loadConversations = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await apiClient.post(`${API_BASE}/api/chat-history/list`, {
        userId
      });

      if (response.data.ok) {
        setConversations(response.data.conversations);
      }
    } catch (error) {
      console.error('Load conversations error:', error);
      // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© ÿ•ÿ¥ÿπÿßÿ± ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸáŸÜÿß
    } finally {
      setIsLoading(false);
    }
  }, [userId]);

  const handleDelete = async (conversationId, e) => {
    e.stopPropagation(); // ŸÖŸÜÿπ ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© ÿπŸÜÿØ ÿßŸÑŸÜŸÇÿ± ÿπŸÑŸâ ÿ≤ÿ± ÿßŸÑÿ≠ÿ∞ŸÅ

    if (!confirm('ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ÿ£ŸÜŸÉ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ Ÿáÿ∞Ÿá ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ©ÿü')) return;

    try {
      const response = await apiClient.post(`${API_BASE}/api/chat-history/delete`, {
        conversationId
      });

      if (response.data.ok) {
        setConversations(prev => prev.filter(c => c._id !== conversationId));
        if (currentConversationId === conversationId) {
          onNewConversation(); // ÿ®ÿØÿ° ŸÖÿ≠ÿßÿØÿ´ÿ© ÿ¨ÿØŸäÿØÿ© ÿ•ÿ∞ÿß ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© ÿßŸÑŸÜÿ¥ÿ∑ÿ©
        }
      }
    } catch (error) {
      console.error('Delete conversation error:', error);
      // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© ÿ•ÿ¥ÿπÿßÿ± ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸáŸÜÿß
    }
  };

  const formatDate = (dateString) => {
    const d = new Date(dateString);
    const now = new Date();
    const diff = now.getTime() - d.getTime(); // ÿßŸÑŸÅÿ±ŸÇ ÿ®ÿßŸÑŸÖŸÑŸÑŸä ÿ´ÿßŸÜŸäÿ©

    const seconds = Math.floor(diff / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);
    const weeks = Math.floor(days / 7);
    const months = Math.floor(days / 30);
    const years = Math.floor(days / 365);

    if (seconds < 60) return 'ÿßŸÑÿ¢ŸÜ';
    if (minutes < 60) return `ŸÖŸÜÿ∞ ${minutes} ÿØŸÇŸäŸÇÿ©`;
    if (hours < 24) return `ŸÖŸÜÿ∞ ${hours} ÿ≥ÿßÿπÿ©`;
    if (days < 7) return `ŸÖŸÜÿ∞ ${days} ŸäŸàŸÖ`;
    if (weeks < 4) return `ŸÖŸÜÿ∞ ${weeks} ÿ£ÿ≥ÿ®Ÿàÿπ`;
    if (months < 12) return `ŸÖŸÜÿ∞ ${months} ÿ¥Ÿáÿ±`;
    return `ŸÖŸÜÿ∞ ${years} ÿ≥ŸÜÿ©`;
  };

  if (isCollapsed) {
    return (
      <div className="
        w-12 h-screen bg-gray-900 border-r border-gray-700
        flex flex-col items-center justify-center
        transition-all duration-300 ease-in-out
      ">
        <button
          onClick={toggleCollapse}
          className="
            p-2 rounded-full text-gray-400 hover:bg-gray-700 hover:text-white
            transition-colors duration-200
          "
          title="ÿ™Ÿàÿ≥Ÿäÿπ ÿßŸÑÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ¨ÿßŸÜÿ®Ÿä"
        >
          <ChevronRight size={20} />
        </button>
      </div>
    );
  }

  return (
    <div className="
      w-80 h-screen bg-gray-900 border-r border-gray-700
      flex flex-col flex-shrink-0
      transition-all duration-300 ease-in-out
    ">
      {/* Header */}
      <div className="
        p-4 border-b border-gray-700
        flex items-center justify-between
        text-white
      ">
        <h2 className="text-xl font-semibold flex items-center gap-2">
          <History size={20} />
          ÿ≥ÿ¨ŸÑ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿßÿ™
        </h2>
        <div className="flex items-center gap-2">
          <button
            onClick={onNewConversation}
            className="
              p-2 rounded-full bg-teal-600 hover:bg-teal-700 text-white
              flex items-center justify-center
              transition-colors duration-200
            "
            title="ŸÖÿ≠ÿßÿØÿ´ÿ© ÿ¨ÿØŸäÿØÿ©"
          >
            <Plus size={18} />
          </button>
          <button
            onClick={toggleCollapse}
            className="
              p-2 rounded-full text-gray-400 hover:bg-gray-700 hover:text-white
              transition-colors duration-200
            "
            title="ÿ∑Ÿä ÿßŸÑÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ¨ÿßŸÜÿ®Ÿä"
          >
            <ChevronLeft size={20} />
          </button>
        </div>
      </div>

      {/* Conversations List */}
      <div className="flex-1 overflow-y-auto p-2 scrollbar-thin scrollbar-thumb-gray-700 scrollbar-track-gray-800">
        {isLoading ? (
          <div className="p-4 text-center text-gray-400">
            <Loader2 className="animate-spin mx-auto text-teal-500 mb-3" size={32} />
            <p className="text-lg">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿßÿ™...</p>
          </div>
        ) : conversations.length === 0 ? (
          <div className="p-4 text-center text-gray-400">
            <MessageSquare className="mx-auto text-gray-500 mb-3" size={48} />
            <p className="text-lg mb-4">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖÿ≠ÿßÿØÿ´ÿßÿ™ ÿ≥ÿßÿ®ŸÇÿ©.</p>
            <button
              onClick={onNewConversation}
              className="
                text-teal-400 hover:text-teal-300 underline
                flex items-center gap-1 mx-auto
              "
            >
              <Plus size={16} /> ÿßÿ®ÿØÿ£ ŸÖÿ≠ÿßÿØÿ´ÿ© ÿ¨ÿØŸäÿØÿ©
            </button>
          </div>
        ) : (
          <div className="space-y-1">
            {conversations.map((conv) => (
              <div
                key={conv._id}
                onClick={() => onSelectConversation(conv._id)}
                className={`
                  group p-3 rounded-lg cursor-pointer transition-all duration-200
                  flex items-center justify-between gap-3
                  ${currentConversationId === conv._id
                    ? 'bg-teal-700/30 border border-teal-600 text-white shadow-md'
                    : 'hover:bg-gray-800 border border-transparent text-gray-200'
                  }
                `}
              >
                <div className="flex-1 min-w-0">
                  <h3 className="font-medium text-sm truncate mb-1">
                    {conv.title || 'ŸÖÿ≠ÿßÿØÿ´ÿ© ÿ®ÿØŸàŸÜ ÿπŸÜŸàÿßŸÜ'} {/* ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± "ŸÖÿ≠ÿßÿØÿ´ÿ© ÿ¨ÿØŸäÿØÿ©" ÿ•ŸÑŸâ "ŸÖÿ≠ÿßÿØÿ´ÿ© ÿ®ÿØŸàŸÜ ÿπŸÜŸàÿßŸÜ" */}
                  </h3>
                  <div className="flex items-center gap-2 text-xs text-gray-400">
                    <span>{formatDate(conv.updatedAt)}</span>
                    <span>‚Ä¢</span>
                    <span>{conv.messages?.length || 0} ÿ±ÿ≥ÿßŸÑÿ©</span>
                  </div>
                </div>

                <button
                  onClick={(e) => handleDelete(conv._id, e)}
                  className="
                    opacity-0 group-hover:opacity-100 text-red-500 hover:text-red-400
                    transition-all duration-200 p-1 rounded-full hover:bg-gray-700
                  "
                  title="ÿ≠ÿ∞ŸÅ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ©"
                >
                  <Trash2 size={18} />
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="
        p-3 border-t border-gray-700
        text-xs text-gray-500 text-center
      ">
        ÿπÿ±ÿ∂ {conversations.length} ŸÖÿ≠ÿßÿØÿ´ÿ©
      </div>
    </div>
  );
}

