import JoeScreen from './JoeScreen';

export default function DashboardLayout() {
  const [isJoeScreenOpen, setIsJoeScreenOpen] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false); // ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÇŸäŸÖÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
  const [progress, setProgress] = useState(0); // ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÇŸäŸÖÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
  const [wsLog, setWsLog] = useState([]); // ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÜŸàÿπ

  const handleTakeover = () => {
    console.log('User has taken over the Joe screen.');
    // ŸáŸÜÿß ŸäŸÖŸÉŸÜŸÉ ÿ•ÿ∂ÿßŸÅÿ© ŸÖŸÜÿ∑ŸÇ ŸÑŸÑÿ™ÿ≠ŸÉŸÖ ŸÅŸä Joe
  };

  const handleCloseJoeScreen = () => {
    setIsJoeScreenOpen(false);
  };

  const handleCommandSubmit = async (data) => {
    setIsProcessing(true);
    setProgress(0);
    setWsLog(prev => [...prev, `Sending command: ${data.commandText}`]);
    console.log('Command submitted from JoeScreen:', data);

    // ŸÖÿ≠ÿßŸÉÿßÿ© ŸÑÿπŸÖŸÑŸäÿ© ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ£ŸÖÿ± ŸàÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
    for (let i = 0; i <= 100; i += 10) {
      await new Promise(resolve => setTimeout(resolve, 200));
      setProgress(i);
      setWsLog(prev => [...prev, `Progress: ${i}%`]);
    }

    await new Promise(resolve => setTimeout(resolve, 500));
    setWsLog(prev => [...prev, `Command processed. Response: "Hello from Joe!"`]);
    setIsProcessing(false);
    setProgress(100);
  };

  return (
    <div className="min-h-screen bg-bgDark">
      <Navbar onToggleJoeScreen={() => setIsJoeScreenOpen(!isJoeScreenOpen)} />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Outlet />
      </main>
      {isJoeScreenOpen && (
        <JoeScreen
          isProcessing={isProcessing}
          progress={progress}
          wsLog={wsLog}
          onTakeover={handleTakeover}
          onClose={handleCloseJoeScreen}
          onSubmitCommand={handleCommandSubmit} // ÿ™ŸÖÿ±Ÿäÿ± ÿØÿßŸÑÿ© ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ£ŸÖÿ±
        />
      )}
    </div>
  );
}

import React, { useState, useRef } from 'react';
import { Send, Maximize2, Minimize2, RefreshCw, Home, ChevronLeft, ChevronRight, Zap } from 'lucide-react';
import axios from 'axios';

export default function EnhancedBrowserControl() {
  const [url, setUrl] = useState('https://www.google.com');
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [command, setCommand] = useState('');
  const [output, setOutput] = useState([]);
  const [history, setHistory] = useState([]);
  const inputRef = useRef(null);

  const API_BASE = import.meta.env.VITE_API_URL;

  const handleNavigate = async (targetUrl = url) => {
    try {
      setIsLoading(true);
      const response = await axios.post(`${API_BASE}/api/browser/navigate`, {
        url: targetUrl
      });

      if (response.data.success) {
        setUrl(targetUrl);
        addOutput(`‚úÖ ÿ™ŸÖ ÿßŸÑÿßŸÜÿ™ŸÇÿßŸÑ ÿ•ŸÑŸâ: ${targetUrl}`, 'success');
      } else {
        addOutput(`‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿßŸÜÿ™ŸÇÿßŸÑ: ${response.data.error}`, 'error');
      }
    } catch (error) {
      addOutput(`‚ùå ÿÆÿ∑ÿ£: ${error.message}`, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendCommand = async () => {
    if (!command.trim()) return;

    try {
      setIsLoading(true);
      setHistory([...history, command]);
      addOutput(`> ${command}`, 'command');

      const response = await axios.post(`${API_BASE}/api/browser/execute`, {
        command: command
      });

      if (response.data.success) {
        addOutput(response.data.result || 'ÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿ£ŸÖÿ± ÿ®ŸÜÿ¨ÿßÿ≠', 'success');
      } else {
        addOutput(`ÿÆÿ∑ÿ£: ${response.data.error}`, 'error');
      }

      setCommand('');
      if (inputRef.current) {
        inputRef.current.focus();
      }
    } catch (error) {
      addOutput(`ÿÆÿ∑ÿ£: ${error.message}`, 'error');
    } finally {
      setIsLoading(false);
    }
  };

  const addOutput = (text, type = 'info') => {
    setOutput(prev => [...prev, { text, type, id: Date.now() }]);
  };

  const clearOutput = () => {
    setOutput([]);
  };

  const quickCommands = [
    { label: 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©', action: () => handleNavigate('https://www.google.com') },
    { label: 'ÿ™ÿ≠ÿØŸäÿ´', action: () => addOutput('ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿµŸÅÿ≠ÿ©', 'success') },
    { label: 'ŸÑŸÑÿÆŸÑŸÅ', action: () => addOutput('ÿ™ŸÖ ÿßŸÑÿ±ÿ¨Ÿàÿπ ŸÑŸÑÿÆŸÑŸÅ', 'success') },
    { label: 'ŸÑŸÑÿ£ŸÖÿßŸÖ', action: () => addOutput('ÿ™ŸÖ ÿßŸÑÿßŸÜÿ™ŸÇÿßŸÑ ŸÑŸÑÿ£ŸÖÿßŸÖ', 'success') }
  ];

  return (
    <div className={`bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 rounded-lg overflow-hidden shadow-2xl ${
      isFullscreen ? 'fixed inset-0 z-50' : ''
    }`}>
      <div className="bg-gradient-to-r from-slate-800 to-slate-700 border-b border-cyan-500/30 p-4">
        <div className="flex items-center justify-between gap-4 flex-wrap">
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 bg-slate-700/50 rounded-lg px-3 py-2 border border-slate-600">
              <input
                type="text"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleNavigate()}
                placeholder="ÿ£ÿØÿÆŸÑ ÿπŸÜŸàÿßŸÜ URL..."
                className="flex-1 bg-transparent text-cyan-400 placeholder-gray-500 outline-none text-sm"
              />
              <button
                onClick={() => handleNavigate()}
                disabled={isLoading}
                className="p-2 hover:bg-slate-600 rounded-lg transition-colors disabled:opacity-50"
                title="ÿßŸÜÿ™ŸÇŸÑ"
              >
                <ChevronRight size={18} className="text-cyan-400" />
              </button>
            </div>
          </div>

          <div className="flex items-center gap-2">
            <button
              onClick={() => setIsFullscreen(!isFullscreen)}
              className="p-2 rounded-lg bg-slate-700 hover:bg-slate-600 text-cyan-400 transition-colors"
              title={isFullscreen ? 'ÿ™ÿµÿ∫Ÿäÿ±' : 'ÿ™ŸÉÿ®Ÿäÿ±'}
            >
              {isFullscreen ? <Minimize2 size={18} /> : <Maximize2 size={18} />}
            </button>
          </div>
        </div>

        <div className="flex gap-2 mt-3 flex-wrap">
          {quickCommands.map((cmd, idx) => (
            <button
              key={idx}
              onClick={cmd.action}
              disabled={isLoading}
              className="px-3 py-1 text-sm bg-slate-600 hover:bg-slate-500 text-cyan-400 rounded-lg transition-colors disabled:opacity-50"
            >
              {cmd.label}
            </button>
          ))}
        </div>
      </div>

      <div className="flex flex-col h-96 md:h-[600px]">
        <div className="flex-1 overflow-y-auto bg-black/30 p-4 space-y-2 font-mono text-sm">
          {output.length === 0 ? (
            <div className="flex items-center justify-center h-full text-gray-500">
              <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿπŸÖŸÑŸäÿßÿ™ ÿ≠ÿ™Ÿâ ÿßŸÑÿ¢ŸÜ...</p>
            </div>
          ) : (
            output.map(line => (
              <div
                key={line.id}
                className={`${
                  line.type === 'success'
                    ? 'text-green-400'
                    : line.type === 'error'
                    ? 'text-red-400'
                    : line.type === 'command'
                    ? 'text-yellow-400'
                    : 'text-gray-400'
                }`}
              >
                {line.text}
              </div>
            ))
          )}
        </div>

        <div className="border-t border-cyan-500/30 bg-slate-800/50 p-4">
          <div className="flex gap-2 mb-3">
            <input
              ref={inputRef}
              type="text"
              value={command}
              onChange={(e) => setCommand(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendCommand()}
              placeholder="ÿ£ÿØÿÆŸÑ ÿ£ŸÖÿ± ÿ£Ÿà ÿßÿ≥ÿ™ÿπŸÑÿßŸÖ..."
              className="flex-1 px-3 py-2 bg-slate-700 text-cyan-400 placeholder-gray-500 rounded-lg border border-slate-600 focus:border-cyan-500 outline-none"
              disabled={isLoading}
            />
            <button
              onClick={handleSendCommand}
              disabled={isLoading || !command.trim()}
              className="px-4 py-2 bg-cyan-500 hover:bg-cyan-600 text-white rounded-lg transition-colors disabled:opacity-50 flex items-center gap-2"
            >
              <Send size={18} />
              <span className="hidden sm:inline">ÿ•ÿ±ÿ≥ÿßŸÑ</span>
            </button>
          </div>

          <div className="flex gap-2 flex-wrap">
            <button
              onClick={clearOutput}
              className="px-3 py-1 text-sm bg-slate-700 hover:bg-slate-600 text-gray-400 rounded-lg transition-colors"
            >
              ŸÖÿ≥ÿ≠ ÿßŸÑÿ≥ÿ¨ŸÑ
            </button>
            <button
              onClick={() => setCommand('help')}
              className="px-3 py-1 text-sm bg-slate-700 hover:bg-slate-600 text-gray-400 rounded-lg transition-colors"
            >
              ŸÖÿ≥ÿßÿπÿØÿ©
            </button>
          </div>
        </div>
      </div>

      <div className="bg-slate-800/50 border-t border-cyan-500/30 px-4 py-2 flex items-center justify-between text-xs text-gray-400">
        <div className="flex items-center gap-2">
          <div className={`w-2 h-2 rounded-full ${
            isLoading ? 'bg-yellow-500 animate-pulse' : 'bg-green-500'
          }`}></div>
          <span>{isLoading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©...' : 'ÿ¨ÿßŸáÿ≤'}</span>
        </div>
        <div className="flex items-center gap-2">
          <Zap size={14} />
          <span>ÿßŸÑÿ£ŸàÿßŸÖÿ± ÿßŸÑŸÖŸÜŸÅÿ∞ÿ©: {history.length}</span>
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Upload, X, FileText, Image, File } from 'lucide-react';

export default function FileUpload({ onFileAnalyzed }) {
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setIsDragging(true);
    } else if (e.type === 'dragleave') {
      setIsDragging(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const files = e.dataTransfer.files;
    if (files && files[0]) {
      handleFile(files[0]);
    }
  };

  const handleFileSelect = (e) => {
    const files = e.target.files;
    if (files && files[0]) {
      handleFile(files[0]);
    }
  };

  const handleFile = async (file) => {
    setSelectedFile(file);
    setUploading(true);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('https://admin.xelitesolutions.com/api/file/upload', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.ok) {
        onFileAnalyzed({
          fileName: data.fileName,
          fileType: data.fileType,
          fileSize: data.fileSize,
          analysis: data.analysis
        });
      } else {
        alert('ÿÆÿ∑ÿ£ ŸÅŸä ÿ±ŸÅÿπ ÿßŸÑŸÖŸÑŸÅ: ' + data.error);
      }
    } catch (error) {
      console.error('Upload error:', error);
      alert('ŸÅÿ¥ŸÑ ÿ±ŸÅÿπ ÿßŸÑŸÖŸÑŸÅ');
    } finally {
      setUploading(false);
      setSelectedFile(null);
    }
  };

  const getFileIcon = () => {
    if (!selectedFile) return <Upload className="w-6 h-6 text-cyan-400 drop-shadow-[0_0_5px_rgba(0,255,255,0.5)]" />;

    const type = selectedFile.type;
    if (type.startsWith('image/')) return <Image className="w-6 h-6 text-fuchsia-400 drop-shadow-[0_0_5px_rgba(255,0,255,0.5)]" />;
    if (type.includes('text') || type.includes('json')) return <FileText className="w-6 h-6 text-cyan-400 drop-shadow-[0_0_5px_rgba(0,255,255,0.5)]" />;
    return <File className="w-6 h-6 text-fuchsia-400 drop-shadow-[0_0_5px_rgba(255,0,255,0.5)]" />;
  };

  return (
    <div
      className={`border-2 border-dashed rounded-lg p-8 text-center transition-all ${
        isDragging
          ? 'border-cyan-500 bg-cyan-500/10 shadow-lg shadow-cyan-900/50'
          : 'border-gray-700 hover:border-fuchsia-500/50'
      }`}
      onDragEnter={handleDrag}
      onDragLeave={handleDrag}
      onDragOver={handleDrag}
      onDrop={handleDrop}
    >
      <input
        type="file"
        id="file-upload"
        className="hidden"
        onChange={handleFileSelect}
        accept=".txt,.md,.json,.js,.mjs,.jsx,.ts,.tsx,.html,.css,.xml,.csv,.jpg,.jpeg,.png,.pdf"
      />

      <label htmlFor="file-upload" className="cursor-pointer">
        <div className="flex flex-col items-center gap-4">
          {getFileIcon()}

          {uploading ? (
            <div className="text-cyan-400 drop-shadow-[0_0_5px_rgba(0,255,255,0.5)]">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-cyan-400 mx-auto mb-2"></div>
              <p>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ±ŸÅÿπ...</p>
            </div>
          ) : selectedFile ? (
            <div>
              <p className="text-white font-medium">{selectedFile.name}</p>
              <p className="text-gray-400 text-sm">{(selectedFile.size / 1024).toFixed(2)} KB</p>
            </div>
          ) : (
            <div>
              <p className="text-textPrimary font-medium mb-2">
                ÿßÿ≥ÿ≠ÿ® ÿßŸÑŸÖŸÑŸÅ ŸáŸÜÿß ÿ£Ÿà ÿßÿ∂ÿ∫ÿ∑ ŸÑŸÑÿßÿÆÿ™Ÿäÿßÿ±
              </p>
              <p className="text-gray-400 text-sm">
                ÿßŸÑÿ£ŸÜŸàÿßÿπ ÿßŸÑŸÖÿØÿπŸàŸÖÿ©: ŸÜÿµŸàÿµÿå ÿµŸàÿ±ÿå PDFÿå ŸÉŸàÿØ
              </p>
            </div>
          )}
        </div>
      </label>
    </div>
  );
}

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { X, RefreshCw, ArrowLeft, ArrowRight, Maximize2, Minimize2, Monitor, Cpu, Activity, AlertCircle } from 'lucide-react';
import useBrowserWebSocket from '../hooks/useBrowserWebSocket'; // ÿßŸÅÿ™ÿ±ÿ∂ Ÿàÿ¨ŸàÿØ Ÿáÿ∞ÿß ÿßŸÑŸÄ Hook

const FullScreenBrowser = ({ onClose }) => {
  const [url, setUrl] = useState('https://google.com'); // URL ÿßŸÑÿ≠ÿßŸÑŸä ÿßŸÑŸÖÿπÿ±Ÿàÿ∂
  const [inputUrl, setInputUrl] = useState('https://google.com'); // URL ŸÅŸä ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ•ÿØÿÆÿßŸÑ
  const canvasRef = useRef(null);
  const [isControlMode, setIsControlMode] = useState(false);
  const [isFullScreen, setIsFullScreen] = useState(false); // ŸÑÿ•ÿØÿßÿ±ÿ© Ÿàÿ∂ÿπ ŸÖŸÑÿ° ÿßŸÑÿ¥ÿßÿ¥ÿ© ŸÑŸÑŸÖÿ™ÿµŸÅÿ≠ ŸÜŸÅÿ≥Ÿá

  // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÄ Hook ÿßŸÑŸÖÿÆÿµÿµ ŸÑŸÄ WebSocket
  const {
    screenshot,
    pageInfo,
    isConnected,
    isLoading, // ÿßŸÅÿ™ÿ±ÿßÿ∂ Ÿàÿ¨ŸàÿØ ÿ≠ÿßŸÑÿ© ÿ™ÿ≠ŸÖŸäŸÑ ŸÖŸÜ ÿßŸÑŸÄ hook
    isNavigating, // ÿßŸÅÿ™ÿ±ÿßÿ∂ Ÿàÿ¨ŸàÿØ ÿ≠ÿßŸÑÿ© ÿ™ŸÜŸÇŸÑ ŸÖŸÜ ÿßŸÑŸÄ hook
    error, // ÿßŸÅÿ™ÿ±ÿßÿ∂ Ÿàÿ¨ŸàÿØ ÿ≠ÿßŸÑÿ© ÿÆÿ∑ÿ£ ŸÖŸÜ ÿßŸÑŸÄ hook
    navigate,
    goBack, // ÿßŸÅÿ™ÿ±ÿßÿ∂ Ÿàÿ¨ŸàÿØ ÿØÿßŸÑÿ© ŸÑŸÑÿπŸàÿØÿ© ŸÖŸÜ ÿßŸÑŸÄ hook
    goForward, // ÿßŸÅÿ™ÿ±ÿßÿ∂ Ÿàÿ¨ŸàÿØ ÿØÿßŸÑÿ© ŸÑŸÑÿ™ŸÇÿØŸÖ ŸÖŸÜ ÿßŸÑŸÄ hook
    reload, // ÿßŸÅÿ™ÿ±ÿßÿ∂ Ÿàÿ¨ŸàÿØ ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ÿØŸäÿ´ ŸÖŸÜ ÿßŸÑŸÄ hook
    click,
    type: typeText,
    scroll,
    pressKey
  } = useBrowserWebSocket();

  // ÿ™ÿ≠ÿØŸäÿ´ ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ•ÿØÿÆÿßŸÑ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± URL ÿßŸÑÿµŸÅÿ≠ÿ©
  useEffect(() => {
    if (pageInfo?.url && pageInfo.url !== inputUrl) {
      setInputUrl(pageInfo.url);
      setUrl(pageInfo.url); // ÿ™ÿ≠ÿØŸäÿ´ URL ÿßŸÑÿ≠ÿßŸÑŸä ÿßŸÑŸÖÿπÿ±Ÿàÿ∂
    }
  }, [pageInfo?.url]);

  // ÿ±ÿ≥ŸÖ ŸÑŸÇÿ∑ÿ© ÿßŸÑÿ¥ÿßÿ¥ÿ© ÿπŸÑŸâ Canvas
  useEffect(() => {
    if (screenshot && canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      const img = new Image();
      img.onload = () => {
        // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ŸÖÿ≥ÿ≠ Canvas ŸÇÿ®ŸÑ ÿßŸÑÿ±ÿ≥ŸÖ ŸÑÿ™ÿ¨ŸÜÿ® ÿßŸÑÿ™ÿØÿßÿÆŸÑ
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.drawImage(img, 0, 0);
      };
      img.src = screenshot;
    }
  }, [screenshot]);

  const handleNavigate = useCallback(() => {
    if (inputUrl && inputUrl !== url) {
      navigate(inputUrl);
    }
  }, [inputUrl, url, navigate]);

  const handleCanvasClick = useCallback((e) => {
    if (!isControlMode) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const scaleX = canvas.width / rect.width;
    const scaleY = canvas.height / rect.height;

    const x = (e.clientX - rect.left) * scaleX;
    const y = (e.clientY - rect.top) * scaleY;

    click(x, y);
  }, [isControlMode, click]);

  const handleCanvasWheel = useCallback((e) => {
    if (!isControlMode) return;
    e.preventDefault(); // ŸÖŸÜÿπ ÿßŸÑÿ™ŸÖÿ±Ÿäÿ± ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä ŸÑŸÑÿµŸÅÿ≠ÿ©
    scroll(e.deltaY);
  }, [isControlMode, scroll]);

  const handleKeyDown = useCallback((e) => {
    if (!isControlMode) return;

    // ŸÖŸÜÿπ ÿßŸÑÿ≥ŸÑŸàŸÉ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä ŸÑŸÑŸÖÿ™ÿµŸÅÿ≠ ŸÑÿ®ÿπÿ∂ ÿßŸÑŸÖŸÅÿßÿ™Ÿäÿ≠
    if (['Enter', 'Backspace', 'Tab', 'ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(e.key)) {
      e.preventDefault();
    }

    if (e.key === 'Enter') {
      pressKey('Enter');
    } else if (e.key === 'Backspace') {
      pressKey('Backspace');
    } else if (e.key === 'Tab') {
      pressKey('Tab');
    } else if (e.key === 'ArrowUp') {
      scroll(-100); // ÿ™ŸÖÿ±Ÿäÿ± ŸÑÿ£ÿπŸÑŸâ
    } else if (e.key === 'ArrowDown') {
      scroll(100); // ÿ™ŸÖÿ±Ÿäÿ± ŸÑÿ£ÿ≥ŸÅŸÑ
    } else if (e.key.length === 1 && !e.ctrlKey && !e.altKey && !e.metaKey) { // ŸÅŸÇÿ∑ ÿßŸÑÿ£ÿ≠ÿ±ŸÅ ÿßŸÑŸÅÿ±ÿØŸäÿ©
      typeText(e.key);
    }
  }, [isControlMode, pressKey, typeText, scroll]);

  // ÿßŸÑÿ™ÿ±ŸÉŸäÿ≤ ÿπŸÑŸâ Canvas ÿπŸÜÿØ ÿ™ŸÅÿπŸäŸÑ Ÿàÿ∂ÿπ ÿßŸÑÿ™ÿ≠ŸÉŸÖ
  useEffect(() => {
    if (isControlMode && canvasRef.current) {
      canvasRef.current.focus();
    }
  }, [isControlMode]);

  // ÿ™ÿ®ÿØŸäŸÑ Ÿàÿ∂ÿπ ŸÖŸÑÿ° ÿßŸÑÿ¥ÿßÿ¥ÿ© ŸÑŸÑŸÖÿ™ÿµŸÅÿ≠
  const toggleFullScreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen().then(() => setIsFullScreen(true));
    } else {
      document.exitFullscreen().then(() => setIsFullScreen(false));
    }
  };

  return (
