          {/* Progress indicator */}
          {isProcessing && (
            <div className="flex items-center gap-2">
              <div className="w-24 h-1 bg-gray-700 rounded-full overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-cyan-400 to-fuchsia-400 transition-all"
                  style={{ width: `${progress}%` }}
                />
