import { useState, useEffect, useRef } from 'react';

export default function VoiceInput({ onTranscript, disabled }) {
  const [isListening, setIsListening] = useState(false);
  const [isSupported, setIsSupported] = useState(false);
  const recognitionRef = useRef(null);

  useEffect(() => {
    // Check if browser supports Web Speech API
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      setIsSupported(true);

      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognition = new SpeechRecognition();

      recognition.continuous = true; // ÿßÿ≥ÿ™ŸÖÿ±ÿßÿ± ÿßŸÑÿßÿ≥ÿ™ŸÖÿßÿπ ÿ®ÿπÿØ ÿßŸÑÿ™ŸàŸÇŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™
      recognition.interimResults = true; // ÿ•ÿ∏Ÿáÿßÿ± ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨ ÿßŸÑŸÖÿ§ŸÇÿ™ÿ©
      recognition.lang = 'ar-SA'; // Arabic
      recognition.maxAlternatives = 1;

      recognition.onresult = (event) => {
        let finalTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; ++i) {
          if (event.results[i].isFinal) {
            finalTranscript += event.results[i][0].transcript;
          }
        }
        // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÜÿµ ÿßŸÑŸÜŸáÿßÿ¶Ÿä ŸÅŸÇÿ∑
        if (finalTranscript) {
          onTranscript(finalTranscript);
        }
        // ŸÑÿß ÿ™ŸàŸÇŸÅ recognition.stop() ŸáŸÜÿßÿå ÿ®ŸÑ ÿØÿπŸá Ÿäÿ≥ÿ™ŸÖÿ± ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ continuous = true
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognition.onend = () => {
        // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ continuous = trueÿå ŸÅÿ•ŸÜ onend Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿØÿπÿßÿ§Ÿáÿß ÿ®ÿπÿØ ÿßŸÑÿ™ŸàŸÇŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™
        // Ÿäÿ¨ÿ® ÿ£ŸÜ ŸÜÿ®ÿØÿ£ ÿßŸÑÿßÿ≥ÿ™ŸÖÿßÿπ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ÿ•ÿ∞ÿß ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ŸäŸÇÿßŸÅŸá ŸäÿØŸàŸäÿßŸã
        if (recognitionRef.current && isListening) {
          recognitionRef.current.start();
        } else {
          setIsListening(false);
        }
      };

      recognitionRef.current = recognition;
    }
  }, [onTranscript, isListening]);

  const toggleListening = () => {
    if (!recognitionRef.current) return;

    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  if (!isSupported) {
    return null;
  }

  return (
    <button
      type="button"
      onClick={toggleListening}
      disabled={disabled}
      className={`
        p-3 rounded-lg transition-all duration-200
        ${isListening 
          ? 'bg-red-500 hover:bg-red-600 animate-pulse' 
          : 'bg-primary hover:bg-primary/80'
        }
        ${disabled ? 'opacity-50 cursor-not-allowed' : ''}
      `}
      title={isListening ? 'ÿ•ŸäŸÇÿßŸÅ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ' : 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿµŸàÿ™Ÿä'}
    >
      {isListening ? 'üî¥' : 'üé§'}
    </button>
  );
}

