const getApiConfig = () => {
  const isDevelopment = import.meta.env.MODE !== 'production';
  return {
    apiBaseUrl: isDevelopment 
      ? 'http://localhost:4000/api/v1'
      : 'https://api.xelitesolutions.com/api/v1',
    wsBaseUrl: isDevelopment
      ? 'ws://localhost:4000'
      : 'wss://api.xelitesolutions.com'
  };
};

const API_CONFIG = getApiConfig();

// State Management with useReducer
const initialState = {
  platforms: [],
  selectedPlatform: 'shopify',
  storeUrl: '',
  authType: 'token',
  apiKey: '',
  apiSecret: '',
  accessToken: '',
  customEndpoints: '',
  loading: false,
  connectionResult: null,
  products: null,
  analysis: null,
  error: '',
  successMessage: '',
  validationErrors: {},
  searchTerm: '',
  filterCategory: 'all',
  currentPage: 1,
  itemsPerPage: 9,
  showApiKey: false,
  showApiSecret: false,
  showAccessToken: false,
  connectionStatus: 'disconnected',
  apiStats: {
    totalRequests: 0,
    successfulRequests: 0,
    failedRequests: 0,
    lastRequestTime: null,
  },
};

function reducer(state, action) {
  switch (action.type) {
    case 'SET_FIELD':
      return { ...state, [action.field]: action.value, error: '', validationErrors: {} };
    case 'SET_LOADING':
      return { ...state, loading: action.value };
    case 'SET_ERROR':
      return { 
        ...state, 
        error: action.value, 
        loading: false, 
        connectionStatus: 'error',
        apiStats: {
          ...state.apiStats,
          failedRequests: state.apiStats.failedRequests + 1,
          lastRequestTime: new Date().toISOString(),
        }
      };
    case 'SET_SUCCESS':
      return { 
        ...state, 
        successMessage: action.value, 
        error: '', 
        loading: false,
        apiStats: {
          ...state.apiStats,
          successfulRequests: state.apiStats.successfulRequests + 1,
          lastRequestTime: new Date().toISOString(),
        }
      };
    case 'SET_PLATFORMS':
      return { ...state, platforms: action.value };
    case 'SET_CONNECTION_RESULT':
      return { 
        ...state, 
        connectionResult: action.value, 
        loading: false, 
        successMessage: 'ÿ™ŸÖ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ŸÜÿ¨ÿßÿ≠!', 
        connectionStatus: 'connected',
        apiStats: {
          ...state.apiStats,
          successfulRequests: state.apiStats.successfulRequests + 1,
          lastRequestTime: new Date().toISOString(),
        }
      };
    case 'SET_PRODUCTS':
      return { ...state, products: action.value, loading: false };
    case 'SET_ANALYSIS':
      return { ...state, analysis: action.value, loading: false };
    case 'SET_VALIDATION_ERRORS':
      return { ...state, validationErrors: action.value };
    case 'RESET_MESSAGES':
      return { ...state, error: '', successMessage: '' };
    case 'SET_CONNECTION_STATUS':
      return { ...state, connectionStatus: action.value };
    case 'TOGGLE_VISIBILITY':
      return { ...state, [action.field]: !state[action.field] };
    case 'INCREMENT_REQUESTS':
      return {
        ...state,
        apiStats: {
          ...state.apiStats,
          totalRequests: state.apiStats.totalRequests + 1,
        }
      };
    default:
      return state;
  }
}

