import { useState, useEffect, useRef, useCallback } from 'react';

const useBrowserWebSocket = () => {
  const [screenshot, setScreenshot] = useState(null);
  const [pageInfo, setPageInfo] = useState({ title: '', url: '' });
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const wsRef = useRef(null);

  // Temporarily disable WebSocket connection until the backend supports it
  useEffect(() => {
    console.log('Browser WebSocket connection temporarily disabled.');
    setIsConnected(false);
    return () => {};
  }, []);

  const navigate = useCallback((url) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      setIsLoading(true);
      wsRef.current.send(JSON.stringify({
        type: 'navigate',
        payload: { url }
      }));
    }
  }, []);

  const click = useCallback((x, y) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'click',
        payload: { x, y }
      }));
    }
  }, []);

  const type = useCallback((text) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'type',
        payload: { text }
      }));
    }
  }, []);

  const scroll = useCallback((deltaY) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'scroll',
        payload: { deltaY }
      }));
    }
  }, []);

  const pressKey = useCallback((key) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'press_key',
        payload: { key }
      }));
    }
  }, []);

  const getScreenshot = useCallback(() => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type: 'get_screenshot' }));
    }
  }, []);

  const startStreaming = useCallback(() => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type: 'start_streaming' }));
    }
  }, []);

  const stopStreaming = useCallback(() => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type: 'stop_streaming' }));
    }
  }, []);

  return {
    screenshot,
    pageInfo,
    isConnected,
    isLoading,
    navigate,
    click,
    type,
    scroll,
    pressKey,
    getScreenshot,
    startStreaming,
    stopStreaming
  };
};

export default useBrowserWebSocket;

// src/hooks/useSessionToken.js
import { useState, useEffect } from 'react';

/**
 * useSessionToken
 * - SSR-safe (Ÿäÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ window)
 * - ŸÖÿ≤ÿßŸÖŸÜÿ© ÿ™ŸÑŸÇÿßÿ¶Ÿäÿ© ÿ®ŸäŸÜ ÿßŸÑÿ™ÿßÿ®ÿßÿ™
 * - ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ™ÿÆÿ≤ŸäŸÜ: localStorage ÿ£Ÿà sessionStorage
 * - ÿÆŸäÿßÿ± ÿßŸÜÿ™Ÿáÿßÿ° ÿµŸÑÿßÿ≠Ÿäÿ© ŸÑŸÑÿ™ŸàŸÉŸÜ (TTL)
 *
 * Usage:
 * const { token, saveToken, clearToken, isAuthenticated } =
 *   useSessionToken({ key: 'sessionToken', storage: 'local', withExpiry: true });
 *
 * saveToken('abc123', 60_000); // Ÿäÿ≠ŸÅÿ∏ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÑŸÖÿØÿ© 60 ÿ´ÿßŸÜŸäÿ© ÿ•ÿ∞ÿß withExpiry=true
 */
export const useSessionToken = (opts = {}) => {
  const {
    key = 'sessionToken',
    storage = 'local',          // 'local' | 'session'
    withExpiry = false,         // ÿπŸÜÿØŸÖÿß true: ŸÜÿÆÿ≤ŸÜ { value, exp }
  } = opts;

  const getStore = () => {
    if (typeof window === 'undefined') return null;
    return storage === 'session' ? window.sessionStorage : window.localStorage;
  };

  const readFromStore = () => {
    try {
      const store = getStore();
      if (!store) return null;
      const raw = store.getItem(key);
      if (!raw) return null;
      if (!withExpiry) return raw;

      // ŸÖÿπ ÿßŸÜÿ™Ÿáÿßÿ° ÿµŸÑÿßÿ≠Ÿäÿ©
      const parsed = JSON.parse(raw);
      if (parsed?.exp && Date.now() > parsed.exp) {
        store.removeItem(key);
        return null;
      }
      return parsed?.value ?? null;
    } catch {
      return null;
    }
  };

  // ÿ™ŸáŸäÿ¶ÿ© Lazy + ÿ¢ŸÖŸÜÿ© ŸÑŸÄ SSR
  const [token, setToken] = useState(() => readFromStore());

  // ŸáŸäÿØÿ±ÿ¥ŸÜ ÿ®ÿπÿØ ÿßŸÑÿ™ÿ±ŸÉŸäÿ® (ŸÅŸä ÿ≠ÿßŸÑ SSR ŸÉÿßŸÜÿ™ null)
  useEffect(() => {
    if (token == null) {
      const existing = readFromStore();
      if (existing != null) setToken(existing);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // ŸÖÿ≤ÿßŸÖŸÜÿ© ÿπÿ®ÿ± ÿßŸÑÿ™ÿßÿ®ÿßÿ™
  useEffect(() => {
    if (typeof window === 'undefined') return;
    const onStorage = (e) => {
      if (e.key !== key) return;
      // ŸÇÿØ ÿ™ŸÉŸàŸÜ ÿßŸÑŸÇŸäŸÖÿ© string ÿ£Ÿà JSON ŸÖÿπ expiry
      const next = readFromStore();
      setToken(next);
    };
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [key, storage, withExpiry]);

  const saveToken = (newToken, ttlMs) => {
    try {
      const store = getStore();
      if (!store) return;
      if (withExpiry && ttlMs && Number.isFinite(ttlMs)) {
        const payload = JSON.stringify({ value: newToken, exp: Date.now() + ttlMs });
        store.setItem(key, payload);
      } else {
        store.setItem(key, String(newToken));
      }
      setToken(newToken);
    } catch {
      // ÿ™ÿ¨ÿßŸáŸÑ ÿ£ÿÆÿ∑ÿßÿ° ÿßŸÑŸÉŸàÿ™ÿß/ÿßŸÑŸàÿµŸàŸÑ
    }
  };

  const clearToken = () => {
    try {
      const store = getStore();
      if (store) store.removeItem(key);
    } catch { /* Ignore quota/access errors */ }
    setToken(null);
  };

  const isAuthenticated = () => !!token;

  return { token, saveToken, clearToken, isAuthenticated };
};

// src/hooks/useSpeechRecognition.js
import { useState, useEffect, useCallback } from 'react';

// ÿ™ÿπÿ±ŸäŸÅ Ÿàÿßÿ¨Ÿáÿ© SpeechRecognition (ŸÑŸÑÿ™ŸàÿßŸÅŸÇ ŸÖÿπ ÿßŸÑŸÖÿ™ÿµŸÅÿ≠ÿßÿ™)
const SpeechRecognition = typeof window !== 'undefined' 
  ? (window.SpeechRecognition || window.webkitSpeechRecognition)
  : null;

export const useSpeechRecognition = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [error, setError] = useState(null);

  // ÿ™ŸáŸäÿ¶ÿ© recognition ÿØÿßÿÆŸÑ useEffect ŸÑÿ∂ŸÖÿßŸÜ ÿ£ŸÜŸáÿß ÿ™ÿ≠ÿØÿ´ ŸÖÿ±ÿ© Ÿàÿßÿ≠ÿØÿ© ŸÅŸÇÿ∑ ÿ®ÿπÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ
  const [recognition, setRecognition] = useState(null);

  useEffect(() => {
    if (SpeechRecognition) {
      const newRecognition = new SpeechRecognition();
      newRecognition.continuous = true; // ÿßŸÑÿßÿ≥ÿ™ŸÖÿ±ÿßÿ± ŸÅŸä ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ ÿ≠ÿ™Ÿâ Ÿäÿ™ŸÖ ÿ•ŸäŸÇÿßŸÅŸá ŸäÿØŸàŸäÿßŸã
      newRecognition.interimResults = true; // ÿπÿ±ÿ∂ ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨ ÿßŸÑŸÖÿ§ŸÇÿ™ÿ© ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ÿ≠ÿØÿ´
      newRecognition.lang = 'ar-EG'; // ÿØÿπŸÖ ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© (ŸäŸÖŸÉŸÜ ÿ™ÿ∫ŸäŸäÿ±Ÿá)
      setRecognition(newRecognition);
    } else {
      setError('Speech Recognition not supported by this browser.');
    }
  }, []);

  const startListening = useCallback(() => {
    if (!recognition) {
      setError('Speech Recognition not supported by this browser.');
      return;
    }

    // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑŸÜÿµ ŸÇÿ®ŸÑ ÿßŸÑÿ®ÿØÿ°
    setTranscript('');
    setError(null);

    recognition.start();
    setIsListening(true);
  }, [recognition]);

  const stopListening = useCallback(() => {
    if (recognition && isListening) {
      recognition.stop();
      setIsListening(false);
    }
  }, [recognition, isListening]);

  useEffect(() => {
    if (!recognition) return;

    recognition.onresult = (event) => {
      let interimTranscript = '';
      let finalTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; ++i) {
        if (event.results[i].isFinal) {
          finalTranscript += event.results[i][0].transcript;
        } else {
          interimTranscript += event.results[i][0].transcript;
        }
      }
      setTranscript(finalTranscript);
    };

    recognition.onerror = (event) => {
      setError(`Speech Recognition Error: ${event.error}`);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    // ÿ™ŸÜÿ∏ŸäŸÅ ÿπŸÜÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ
    return () => {
      if (recognition) {
        recognition.stop();
      }
    };
  }, [recognition]);

  return {
    isListening,
    transcript,
    error,
    startListening,
    stopListening,
  };
};

import { useState, useEffect, useCallback } from 'react';
import { getActivityStream } from '../api/system';
import ActivityTable from '../components/ActivityTable';
import { RefreshCw, Clock, AlertCircle, Info } from 'lucide-react'; // ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ÿ£ŸäŸÇŸàŸÜÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©

// ÿ™ÿπÿ±ŸäŸÅ ÿ´ÿßÿ®ÿ™ ŸÑŸÅÿ™ÿ±ÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä ÿ®ÿßŸÑŸÖŸÑŸÑŸä ÿ´ÿßŸÜŸäÿ©
const AUTO_REFRESH_INTERVAL = 30000; // 30 ÿ´ÿßŸÜŸäÿ©

export default function Activity() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAutoRefreshing, setIsAutoRefreshing] = useState(false); // ÿ≠ÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä

  // ÿØÿßŸÑÿ© ŸÑÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÜÿ¥ÿ∑ÿ©ÿå ÿ™ŸÖ ÿ™ÿ∫ŸÑŸäŸÅŸáÿß ÿ®ŸÄ useCallback ŸÑÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑÿ£ÿØÿßÿ°
  const fetchActivity = useCallback(async () => {
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ŸáŸÜÿßŸÉ ÿ£ÿ≠ÿØÿßÿ´ ÿ≥ÿßÿ®ŸÇÿ©ÿå ŸÑÿß ŸÜÿ∏Ÿáÿ± ÿ¥ÿßÿ¥ÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿßŸÖŸÑÿ©
    if (events.length === 0) {
      setLoading(true);
    }
    setError(null); // ŸÖÿ≥ÿ≠ ÿ£Ÿä ÿ£ÿÆÿ∑ÿßÿ° ÿ≥ÿßÿ®ŸÇÿ©

    try {
      const data = await getActivityStream();
      setEvents(data.events || []);
    } catch (err) {
      console.error("Failed to fetch activity stream:", err);
      setError(err.message || "An unknown error occurred.");
    } finally {
      setLoading(false);
    }
  }, [events.length]); // Ÿäÿπÿ™ŸÖÿØ ÿπŸÑŸâ events.length ŸÑÿ™ÿ≠ÿØŸäÿØ ŸÖÿß ÿ•ÿ∞ÿß ŸÉÿßŸÜ Ÿäÿ¨ÿ® ÿ•ÿ∏Ÿáÿßÿ± ÿ¥ÿßÿ¥ÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÉÿßŸÖŸÑÿ©

  // ÿ™ÿ£ÿ´Ÿäÿ± ÿ¨ÿßŸÜÿ®Ÿä ŸÑÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÜÿ¥ÿ∑ÿ© ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ
  useEffect(() => {
    fetchActivity();
  }, [fetchActivity]); // Ÿäÿπÿ™ŸÖÿØ ÿπŸÑŸâ fetchActivity

  // ÿ™ÿ£ÿ´Ÿäÿ± ÿ¨ÿßŸÜÿ®Ÿä ŸÑŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä
  useEffect(() => {
    let intervalId;
    if (isAutoRefreshing) {
      intervalId = setInterval(() => {
        console.log("Auto-refreshing activity stream...");
        fetchActivity();
      }, AUTO_REFRESH_INTERVAL);
    }

    // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™ ÿπŸÜÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ ÿ£Ÿà ÿ•ŸäŸÇÿßŸÅ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä
    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }
    };
  }, [isAutoRefreshing, fetchActivity]);

  // ÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸàŸÑŸäÿ© (ÿπŸÜÿØŸÖÿß ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿ≥ÿßÿ®ŸÇÿ©)
  if (loading && events.length === 0) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-neonGreen mx-auto"></div>
          <p className="mt-4 text-textDim">Loading activity stream...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-white">Activity Stream</h1>
          <p className="text-textDim mt-1">Recent Joe & System Events</p>
        </div>
        <div className="flex items-center gap-2">
          {/* ÿ≤ÿ± ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸäÿØŸàŸä */}
          <button
            onClick={fetchActivity}
            disabled={loading} // ÿ™ÿπÿ∑ŸäŸÑ ÿßŸÑÿ≤ÿ± ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ
            className="btn-secondary flex items-center gap-2"
          >
            {loading && events.length > 0 ? ( // ŸÖÿ§ÿ¥ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿÆŸÅŸäŸÅ ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ŸáŸÜÿßŸÉ ÿ®ŸäÿßŸÜÿßÿ™ ÿ≥ÿßÿ®ŸÇÿ©
              <RefreshCw size={16} className="animate-spin" />
            ) : (
              <RefreshCw size={16} />
            )}
            {loading && events.length > 0 ? 'Refreshing...' : 'Refresh'}
          </button>

          {/* ÿ≤ÿ± ÿ™ÿ®ÿØŸäŸÑ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä */}
          <button
            onClick={() => setIsAutoRefreshing(!isAutoRefreshing)}
            className={`btn-secondary flex items-center gap-2 ${isAutoRefreshing ? 'bg-neonGreen/20 text-neonGreen border-neonGreen' : ''}`}
          >
            <Clock size={16} />
            {isAutoRefreshing ? 'Auto-refresh ON' : 'Auto-refresh OFF'}
          </button>
        </div>
      </div>

      {/* ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£ */}
      {error && (
        <div className="card bg-red-500/10 border border-red-500/50 flex items-center gap-2 p-4 rounded-lg">
          <AlertCircle size={20} className="text-red-400" />
          <p className="text-red-400">Error loading activity: {error}</p>
        </div>
      )}

      {/* ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑŸÅÿßÿ±ÿ∫ÿ© */}
      {!loading && !error && events.length === 0 && (
        <div className="card bg-gray-700/20 border border-gray-600/50 flex flex-col items-center justify-center p-8 rounded-lg text-center">
          <Info size={48} className="text-gray-400 mb-4" />
          <p className="text-lg font-medium text-textDim">No activity events to display.</p>
          <p className="text-sm text-gray-500 mt-2">Check back later or try refreshing the stream.</p>
        </div>
      )}

      {/* ÿ¨ÿØŸàŸÑ ÿßŸÑÿ£ŸÜÿ¥ÿ∑ÿ© */}
      {events.length > 0 && <ActivityTable events={events} />}
    </div>
  );
}

