const API_BASE = import.meta.env.VITE_API_BASE || 'https://admin.xelitesolutions.com';

// Reducer for build process state
const buildReducer = (state, action) => {
  switch (action.type) {
    case 'START_BUILD':
      return {
        ...state,
        building: true,
        buildProgress: 0,
        buildLogs: [],
        buildResult: null,
        error: '',
      };
    case 'ADD_LOG':
      return {
        ...state,
        buildLogs: [...state.buildLogs, action.payload],
      };
    case 'SET_PROGRESS':
      return {
        ...state,
        buildProgress: action.payload,
      };
    case 'SET_RESULT':
      return {
        ...state,
        buildResult: action.payload,
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload,
      };
    case 'FINISH_BUILD':
      return {
        ...state,
        building: false,
      };
    case 'RESET_FORM':
      return {
        ...state,
        title: '',
        description: '',
        features: '',
      };
    default:
      return state;
  }
};

