/**
 * Reducer for managing form state (add/edit user)
 */
const formReducer = (state, action) => {
  switch (action.type) {
    case 'UPDATE_FIELD':
      return { ...state, [action.field]: action.value };
    case 'SET_FORM_DATA':
      return { ...action.payload };
    case 'RESET_FORM':
      return { email: '', phone: '', role: 'user', status: 'active' };
    default:
      return state;
  }
};

