import { useState, useEffect } from 'react';
import { getAdminUsers } from '../api/system';
import UsersTable from '../components/UsersTable';
import CardStat from '../components/CardStat';

export default function Users() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await getAdminUsers();
      setData(response);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  if (loading && !data) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-neonGreen mx-auto"></div>
          <p className="mt-4 text-textDim">Loading users...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-white">Users & Sessions</h1>
          <p className="text-textDim mt-1">Admin & User Management</p>
        </div>
        <button
          onClick={fetchUsers}
          disabled={loading}
          className="btn-secondary"
        >
          {loading ? 'Refreshing...' : 'Refresh'}
        </button>
      </div>

      {error && (
        <div className="card bg-red-500/10 border border-red-500/50">
          <p className="text-red-400">Error loading users: {error}</p>
        </div>
      )}

      {data?.stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <CardStat
            title="Active Now"
            value={data.stats.totalActiveNow || 0}
            icon="üü¢"
          />
          <CardStat
            title="Super Admins"
            value={data.stats.totalSupers || 0}
            icon="üëë"
          />
          <CardStat
            title="Admins"
            value={data.stats.totalAdmins || 0}
            icon="üõ°Ô∏è"
          />
          <CardStat
            title="Users"
            value={data.stats.totalUsers || 0}
            icon="üë§"
          />
        </div>
      )}

      <UsersTable users={data?.users || []} />
    </div>
  );
}

// dashboard-x/src/utils/websocket.js
import config from '../config.js';

let ws = null;
let reconnectInterval = null;

export const connectWebSocket = (onMessage, onOpen, onClose) => {
  const token = localStorage.getItem('sessionToken');
  const wsUrl = `${config.wsBaseUrl}/ws/browser`;

  ws = new WebSocket(wsUrl);

  ws.onopen = () => {
    console.log('‚úÖ WebSocket connected');
    if (onOpen) onOpen();
  };

  ws.onmessage = (event) => {
    if (onMessage) onMessage(JSON.parse(event.data));
  };

  ws.onclose = () => {
    console.warn('‚ùå WebSocket disconnected');
    if (onClose) onClose();

    // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ™ŸÑŸÇÿßÿ¶ŸäŸãÿß ÿ®ÿπÿØ 5 ÿ´ŸàÿßŸÜŸä
    clearTimeout(reconnectInterval);
    reconnectInterval = setTimeout(() => {
      console.log('üîÑ Reconnecting WebSocket...');
      connectWebSocket(onMessage, onOpen, onClose);
    }, 5000);
  };

  ws.onerror = (error) => {
    console.error('WebSocket error:', error);
  };

  return ws;
};

export const disconnectWebSocket = () => {
  clearTimeout(reconnectInterval);
  if (ws) {
    ws.close();
    ws = null;
  }
};

