/**
 * ÿ™ŸáŸäÿ¶ÿ© JOEngine AGI
 */
async function initializeJOEngine() {
  if (joengineInitialized) return;

  console.log('\nüöÄ Initializing JOEngine AGI...');

  try {
    // ÿ•ŸÜÿ¥ÿßÿ° Reasoning Engine
    reasoningEngine = new ReasoningEngine({
      openaiApiKey: process.env.OPENAI_API_KEY,
      model: process.env.OPENAI_MODEL || 'gpt-4-turbo-preview'
    });

    // ÿ•ŸÜÿ¥ÿßÿ° Tools System
    toolsSystem = new ToolsSystem();

    // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ£ÿØŸàÿßÿ™
    const browserTool = new BrowserTool();
    toolsSystem.registerTool('browser', browserTool);

    const codeTool = new CodeTool();
    toolsSystem.registerTool('code', codeTool);

    // ÿ•ŸÜÿ¥ÿßÿ° Agent Loop
    agentLoop = new AgentLoop(reasoningEngine, toolsSystem);

    // ÿ®ÿØÿ° Agent Loop
    await agentLoop.start();

    joengineInitialized = true;
    console.log('‚úÖ JOEngine AGI initialized successfully!\n');
  } catch (error) {
    console.error('‚ùå Failed to initialize JOEngine AGI:', error.message);
    console.log('‚ö†Ô∏è  Continuing with basic worker functionality...\n');
  }
}

